    317 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1284',
      'content' => 'Dari Anas bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Berjihadlah melawan kaum musyrikin dengan hartamu, jiwamu dan lidahmu." Riwayat Ahmad dan Nasa\'i. Hadits shahih menurut Hakim.',
      'arabic' => 'وَعَنْ أَنَسٍ رضي الله عنه أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:  جَاهِدُوا اَلْمُشْرِكِينَ بِأَمْوَالِكُمْ, وَأَنْفُسِكُمْ, وَأَلْسِنَتِكُمْ   رَوَاهُ أَحْمَدُ, وَالنَّسَائِيُّ, وَصَحَّحَهُ اَلْحَاكِمُ',
      'id' => 'BM.1284',
    ),
    318 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1285',
      'content' => 'Dari \'Aisyah r.a: Aku berkata: Wahai Rasulullah, apakah perempuan wajib berjihad?. Beliau menjawab: "Ya, jihad tanpa ada peperangan di dalamnya, yaitu haji dan umrah." Riwayat Ibnu Majah dan asalnya dalam kitab Bukhari.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  قُلْتُ: يَا رَسُولَ اَللَّهِ ! عَلَى اَلنِّسَاءِ جِهَادٌ? قَالَ: نَعَمْ جِهَادٌ لَا قِتَالَ فِيهِ, اَلْحَجُّ وَالْعُمْرَةُ  رَوَاهُ اِبْنُ مَاجَه وَأَصْلُهُ فِي اَلْبُخَارِيِّ',
      'id' => 'BM.1285',
    ),
    319 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1286',
      'content' => 'Abdullah Ibnu Umar -Semoga Allah meridlainya- berkata: Ada seseorang menghadap Rasulullah Shallallaahu \'alaihi wa Sallam meminta izin ikut berjihad perang. Beliau bertanya: "Apakah kedua orang tuamu masih hidup?". Ia menjawab: Ya. Beliau bersabda: "Kalau begitu, berjihadlah untuk kedua orang tuamu." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بْنِ عَمْرِوٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  جَاءَ رَجُلٌ إِلَى اَلنَّبِيِّ صلى الله عليه وسلم يَسْتَأْذِنُهُ فِي اَلْجِهَادِ فَقَالَ:  أَ  حَيٌّ وَالِدَاكَ? , قَالَ: نَعَمْ قَالَ:  فَفِيهِمَا فَجَاهِدْ  مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1286',
    ),
    320 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1287',
      'content' => 'Ahmad dan Abu Dawud juga meriwayatkan hadits serupa dan diterima dari Abu Said dengan tambahan: "Pulanglah dan mintalah izin kepada mereka. Jika mereka mengizinkan, berjihadlah, dan jika tidak, berbaktilah kepada mereka berdua."',
      'arabic' => 'وَلِأَحْمَدَ, وَأَبِي دَاوُدَ: مِنْ حَدِيثِ أَبِي سَعِيدٍ نَحْوُهُ, وَزَادَ:  اِرْجِعْ فَاسْتَأْذِنْهُمَا, فَإِنْ أَذِنَا لَكَ; وَإِلَّا فَبِرَّهُمَا',
      'id' => 'BM.1287',
    ),
    321 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1288',
      'content' => 'Dari Jarir -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Aku terlepas tanggung jawab dan diterima dari setiap orang muslim yang tinggal di antara kaum musyrikin." Riwayat Imam Tiga. Sanadnya shahih. Bukhari lebih menilai sebagai hadits mursal.',
      'arabic' => 'وَعَنْ جَرِيرٍ الْبَجَلِيِّ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  أَنَا بَرِئٌ مِنْ كُلِّ مُسْلِمٍ يُقِيمُ بَيْنَ اَلْمُشْرِكِينَ   رَوَاهُ اَلثَّلَاثَةُ وَإِسْنَادُهُ صَحِيحٌ وَرَجَّحَ اَلْبُخَارِيُّ إِرْسَالَهُ',
      'id' => 'BM.1288',
    ),
