    337 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1301',
      'content' => 'Abu Ayyub -Semoga Allah meridlainya- berkata: Ayat ini sebenarnya diturunkan untuk kami golongan Anshor, yaitu firman-Nya artinya = Dan janganlah kamu menjatuhkan diri kamu sendiri ke dalam kebinasaan. Abu Ayyub mengucapkan firman itu sebagai bantahan terhadap orang yang menyalahkan seseorang yang menyerbu barisan tentara Romawi sehingga masuk di antara mereka. Riwayat Imam Tiga. Hadits shahih menurut Tirmidzi, Ibnu Hibban dan Hakim.',
      'arabic' => 'وَعَنْ أَبِي أَيُّوبَ رضي الله عنه قَالَ: إِنَّمَا أُنْزِلَتْ هَذِهِ اَلْآيَةُ فِينَا مَعْشَرَ اَلْأَنْصَارِ, يَعْنِي:وَلَا تُلْقُوا بِأَيْدِيكُمْ إِلَى اَلتَّهْلُكَةِ قَالَهُ رَدًّا عَلَى مَنْ أَنْكَرَ عَلَى مَنْ حَمَلَ عَلَى صَفِ اَلرُّومِ حَتَّى دَخَلَ فِيهِمْ رَوَاهُ اَلثَّلَاثَةُ, وَصَحَّحَهُ اَلتِّرْمِذِيُّ, وَابْنُ حِبَّانَ, وَالْحَاكِمُ',
      'id' => 'BM.1301',
    ),
    338 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1302',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam pernah membakar dan memotong pohon kurma Banu Nadlir. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  حَرَقَ رَسُولُ اَللَّهِ صلى الله عليه وسلم نَخْلَ بَنِي اَلنَّضِيرِ, وَقَطَعَ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1302',
    ),
    339 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1303',
      'content' => 'Dari Ubadah Ibnu al-Shomit -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Janganlah engkau berkhianat terhadap harta rampasan perang, karena balasan bagi pelakunya ialah api neraka dan kehinaan di dunia dan akhirat." Riwayat Ahmad dan Nasa\'i. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْ عُبَادَةَ بْنِ اَلصَّامِتِ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  لَا تَغُلُّوا; فَإِنَّ اَلْغُلُولَ نَارٌ وَعَارٌ عَلَى أَصْحَابِهِ فِي اَلدُّنْيَا وَالْآخِرَةِ   رَوَاهُ أَحْمَدُ, وَالنَّسَائِيُّ, وَصَحَّحَهُ ابْنُ حِبَّانَ',
      'id' => 'BM.1303',
    ),
    340 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1304',
      'content' => 'Dari \'Auf Ibnu Malik -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam menetapkan harta rampasan perang itu bagi sang pembunuh. Riwayat Abu Dawud dan asalnya dalam riwayat Muslim.',
      'arabic' => 'وَعَنْ عَوْفِ بْنِ مَالِكٍ رضي الله عنه  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَضَى بِالسَّلَبِ لِلْقَاتِلِ   رَوَاهُ أَبُو دَاوُدَ وَأَصْلُهُ عِنْدَ مُسْلِمٍ',
      'id' => 'BM.1304',
    ),
    341 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1305',
      'content' => 'Dari Abdurrahman Ibnu \'Auf -Semoga Allah meridlainya- tentang kisah pembunuhan Abu Jahal. Ia berkata: Mereka berdua Mu\'awwidz dan Mu\'adz saling berlomba memancungnya, hingga mereka membunuhnya. Kemudian mereka kembali kepada Rasulullah Shallallaahu \'alaihi wa Sallam dan memberitahukan kepada beliau. Maka beliau bertanya: "Siapakah di antara kamu berdua yang membunuhnya? Apakah kalian sudah membersihkan pedang kalian?". Mereka menjawab: Belum. Perawi berkata: Lalu beliau memeriksa pedang mereka dan bersabda: "Kalian berdua telah membunuhnya." Kemudian beliau memutuskan bahwa harta rampasannya untuk Mu\'adz Ibnu Amar Ibnu al-Jamuh. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ عَبْدِ اَلرَّحْمَنِ بْنِ عَوْفٍ رضي الله عنه فِي - قِصَّةِ قَتْلِ أَبِي جَهْلٍ - قَالَ:  فَابْتَدَرَاهُ بِسَيْفَيْهِمَا حَتَّى قَتَلَاهُ, ثُمَّ انْصَرَفَا إِلَى رَسُولِ اَللَّهِ صلى الله عليه وسلم فَأَخْبَرَاهُ, فَقَالَ: أَيُّكُمَا قَتَلَهُ? هَلْ مَسَحْتُمَا سَيْفَيْكُمَا ? قَالَا: لَا قَالَ: فَنَظَرَ فِيهِمَا, فَقَالَ: كِلَاكُمَا قَتَلَهُ, سَلْبُهُ لِمُعَاذِ بْنِ عَمْرِوِ بْنِ اَلْجَمُوحِ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1305',
    ),
    342 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1306',
      'content' => 'Dari Makhul -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam memasang alat pelempar batu menghadap ke penduduk Thaif. Riwayat Abu Dawud dalam hadits-hadits mursal. Para perawinya dapat dipercaya. Hadits maushul menurut Uqoily dengan sanad lemah dan diterima dari Ali radliyallahu \'anhu',
      'arabic' => 'وَعَنْ مَكْحُولٍ;  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم نَصَبَ اَلْمَنْجَنِيقَ عَلَى أَهْلِ اَلطَّائِفِ   أَخْرَجَهُ أَبُو دَاوُدَ فِي اَلْمَرَاسِيلِ وَرِجَالُهُ ثِقَاتٌ. وَوَصَلَهُ الْعُقَيْلِيُّ بِإِسْنَادٍ ضَعِيفٍ عَنْ عَلِيٍّ رضي الله عنه',
      'id' => 'BM.1306',
    ),
    343 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1307',
      'content' => 'Dari Anas -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam memasuki kota Mekkah dengan mengenakan perisai di kepala. Ketika beliau melepaskannya, ada seseorang datang dan berkata: Ibnu Khathal masih bergantung pada tirai Ka\'bah. Lalu beliau bersabda: "Bunuhlah dia." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَنَسٍ رضي الله عنه  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم دَخَلَ مَكَّةَ وَعَلَى رَأْسِهِ اَلْمِغْفَرُ, فَلَمَّا نَزَعَهُ جَاءَهُ رَجُلٌ, فَقَالَ: ابْنُ خَطَلٍ مُتَعَلِّقٌ بِأَسْتَارِ اَلْكَعْبَةِ, فَقَالَ: اُقْتُلُوهُ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1307',
    ),
    344 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1308',
      'content' => 'Dari Said Ibnu Jubair -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam memerintahkan untuk membunuh tiga orang pada waktu perang Badar dengan dingin yaitu dengan mengikat mereka dan memanahnya. Riwayat Abu Dawud dalam hadits-hadits mursal dan para perawinya dapat dipercaya.',
      'arabic' => 'وَعَنْ سَعِيدِ بْنِ جُبَيْرٍ;  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَتَلَ يَوْمَ بَدْرٍ ثَلَاثَةً صَبْراً   أَخْرَجَهُ أَبُو دَاوُدَ فِي اَلْمَرَاسِيلِ وَرِجَالُهُ ثِقَاتٌ',
      'id' => 'BM.1308',
    ),
    345 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1309',
      'content' => 'Dari Imran Ibnu Hushoin -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam pernah menebus dua orang laki-laki muslim dengan seorang laki-laki musyrik. Riwayat Tirmidzi. Hadits shahih dan asalnya dalam riwayat Muslim.',
      'arabic' => 'وَعَنْ عِمْرَانَ بْنِ حُصَيْنٍ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم فَدَى رَجُلَيْنِ مِنْ اَلْمُسْلِمِينَ بِرَجُلٍ مِنْ اَلْمُشْرِكِينَ   أَخْرَجَهُ اَلتِّرْمِذِيُّ وَصَحَّحَهُ وَأَصْلُهُ عِنْدَ مُسْلِمٍ',
      'id' => 'BM.1309',
    ),
    346 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Mandi dan Hukum Junub',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 131',
      'content' => 'dan diterima dari Ummu Salamah -Semoga Allah meridlainya- berkata: Aku bertanya wahai Rasulullah sungguh aku ini wanita yang mengikat rambut kepalaku. Apakah aku harus membukanya untuk mandi jinabat؟ Dalam riwayat lain disebutkan: Dan mandi dan diterima dari haid؟ Nabi menjawab: "Tidak tapi kamu cukup mengguyur air di atas kepalamu tiga kali." Riwayat Muslim.',
      'arabic' => 'وَعَنْ أُمِّ سَلَمَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  قُلْتُ: يَا رَسُولَ اَللَّهِ إِنِّي اِمْرَأَةٌ أَشُدُّ شَعْرَ رَأْسِي أَفَأَنْقُضُهُ لِغُسْلِ اَلْجَنَابَةِ؟ وَفِي رِوَايَةٍ: وَالْحَيْضَةِ؟ فَقَالَ: لَا إِنَّمَا يَكْفِيكِ أَنْ تَحْثِي عَلَى رَأْسِكِ ثَلَاثَ حَثَيَاتٍ   رَوَاهُ مُسْلِم',
      'id' => 'BM.131',
    ),
    347 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1310',
      'content' => 'Dari Shahar Ibnu al-Ailah bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Sesungguhnya suatu kaum bila mereka masuk Islam, berarti telah menyelamatkan darah dan harta mereka." Riwayat Abu Dawud dan para perawinya dapat dipercaya.',
      'arabic' => 'وَعَنْ صَخْرِ بْنِ اَلْعَيْلَةِ رضي الله عنه أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:  إِنَّ اَلْقَوْمَ إِذَا أَسْلَمُوا؛ أَحْرَزُوا دِمَاءَهُمْ وَأَمْوَالَهُمْ   أَخْرَجَهُ أَبُو دَاوُدَ, وَرِجَالُهُ مُوَثَّقُونَ',
      'id' => 'BM.1310',
    ),
    348 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1311',
      'content' => 'Dari Jubair Ibnu Muth\'im -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda mengenai para tawanan perang Badar: "Sekiranya Muth\'im Ibnu \'Ady masih hidup, kemudian berbicara kepadaku tentang pelepasan orang-orang busuk ini, aku akan serahkan mereka kepadanya." Riwayat Bukhari',
      'arabic' => 'وَعَنْ جُبَيْرِ بْنِ مُطْعَمٍ رضي الله عنه أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ فِي أُسَارَى بَدْرٍ:  لَوْ كَانَ اَلْمُطْعَمُ بْنُ عَدِيٍّ حَيًّا, ثُمَّ كَلَّمَنِي فِي هَؤُلَاءِ اَلنَّتْنَى لَتَرَكْتُهُمْ لَهُ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.1311',
    ),
    349 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1312',
      'content' => 'Abu Said al-Khudry -Semoga Allah meridlainya- berkata: Kami mendapatkan beberapa tawanan yang bersuami pada perang Authas. Para shahabat kesulitan, lalu Allah menurunkan ayat: artinya = Wanita-wanita yang bersuami haram untukmu, kecuali budak-budak yang engkau miliki-ayat. Riwayat Muslim.',
      'arabic' => 'وَعَنْ أَبِي سَعِيدٍ اَلْخُدْرِيِّ رضي الله عنه قَالَ:  أَصَبْنَا سَبَايَا يَوْمَ أَوْطَاسٍ لَهُنَّ أَزْوَاجٌ, فَتَحَرَّجُوا, فَأَنْزَلَ اَللَّهُ تَعَالَى:وَالْمُحْصَنَاتُ مِنَ اَلنِّسَاءِ إِلَّا مَا مَلَكَتْ أَيْمَانُكُمْ   أَخْرَجَهُ مُسْلِمٌ',
      'id' => 'BM.1312',
    ),
    350 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1313',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam pernah mengirim angkatan perang, dan aku termasuk di dalamnya, menuju Najd. Mereka memperoleh rampasan unta yang banyak. Bagian mereka masing-masing dua belas unta, di tambah satu unta. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  بَعَثَ رَسُولُ اَللَّهِ صلى الله عليه وسلم سَرِيَّةٍ وَأَنَا فِيهِمْ, قِبَلَ نَجْدٍ, فَغَنِمُوا إِبِلاً كَثِيرَةً, فَكَانَتْ سُهْمَانُهُمْ اِثْنَيْ عَشَرَ بَعِيراً, وَنُفِّلُوا بَعِيراً بَعِيراً   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1313',
    ),
    351 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1314',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam membagi harta rampasan perang Khaibar, dua bagian untuk kuda dan satu bagian untuk orangnya. (HR. Muttafaqun \'Alaih dan lafadznya menurut Bukhari).',
      'arabic' => 'وَعَنْهُ قَالَ:  قَسَمَ رَسُولُ اَللَّهِ صلى الله عليه وسلم يَوْمَ خَيْبَرَ لِلْفَرَسِ سَهْمَيْنِ, وَلِلرَّاجِلِ سَهْمًا   مُتَّفَقٌ عَلَيْهِ وَاللَّفْظُ لِلْبُخَارِيِّ',
      'id' => 'BM.1314',
    ),
    352 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1315',
      'content' => 'Menurut riwayat Abu Dawud: Beliau membagi untuk orang dan kudanya tiga bagian, dua bagian untuk kudanya dan satu bagian untuknya.',
      'arabic' => 'وَلِأَبِي دَاوُدَ:  أَسْهَمَ لِرَجُلٍ وَلِفَرَسِهِ ثَلَاثَةَ أَسْهُمٍ: سَهْمَيْنِ لِفَرَسِهِ, وَسَهْماً لَهُ',
      'id' => 'BM.1315',
    ),
    353 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1316',
      'content' => 'Ma\'an Ibnu Yazid berkata: Aku mendengar Rasulullah Shallallaahu \'alaihi wa Sallam bersabda: "Tidak ada tambahan bagian kecuali setelah seperlima." Riwayat Ahmad dan Abu Dawud. Hadits shahih menurut Thahawy.',
      'arabic' => 'وَعَنْ مَعْنِ بْنِ يَزِيدَ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  سَمِعْتَ رَسُولَ اَللَّهِ صلى الله عليه وسلم يَقُولُ: لَا نَفْلَ إِلَّا بَعْدَ اَلْخُمُسِ   رَوَاهُ أَحْمَدُ , وَأَبُو دَاوُدَ, وَصَحَّحَهُ اَلطَّحَاوِيُّ',
      'id' => 'BM.1316',
    ),
    354 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1317',
      'content' => 'Habib Ibnu Maslamah -Semoga Allah meridlainya- berkata: Aku menyaksikan Rasulullah Shallallaahu \'alaihi wa Sallam memberi tambahan seperempat waktu berangkat perang dan sepertiga waktu pulang. Riwayat Abu Dawud. Hadits shahih menurut Ibnu al-Jarud, Ibnu Hibban dan Hakim.',
      'arabic' => 'وَعَنْ حَبِيبِ بْنِ مَسْلَمَةٍ رضي الله عنه قَالَ:  شَهِدْتُ رَسُولُ اَللَّهِ صلى الله عليه وسلم نَفَّلَ اَلرُّبْعَ فِي اَلْبَدْأَةِ, وَالثُّلُثَ فِي اَلرَّجْعَةِ   رَوَاهُ أَبُو دَاوُدَ, وَصَحَّحَهُ ابْنُ اَلْجَارُودِ, وَابْنُ حِبَّانَ, وَالْحَاكِمُ',
      'id' => 'BM.1317',
    ),
    355 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1318',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam memberi tambahan khusus kepada sebagian tentara yang beliau kirim, selain bagian resmi para prajurit. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  كَانَ رَسُولُ اَللَّهِ صلى الله عليه وسلم يُنَفِّلُ بَعْضَ مَنْ يَبْعَثُ مِنْ اَلسَّرَايَا لِأَنْفُسِهِمْ خَاصَّةً, سِوَى قَسْمِ عَامَّةِ اَلْجَيْشِ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1318',
    ),
    356 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1319',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Kami pernah memperoleh madu dan anggur dalam peperangan kami, lalu kami makan dan tidak kami laporkan. Riwayat Bukhari. Menurut riwayat Abu Dawud: Tidak diambil seperlima darinya. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْهُ قَالَ:  كُنَّا نُصِيبُ فِي مَغَازِينَا اَلْعَسَلَ وَالْعِنَبَ , فَنَأْكُلُهُ وَلَا نَرْفَعُهُ   رَوَاهُ اَلْبُخَارِيُّ . وَلِأَبِي دَاوُدَ: فَلَمْ يُؤْخَذْ مِنْهُمْ اَلْخُمُسُ  وَصَحَّحَهُ ابْنُ حِبَّانَ',
      'id' => 'BM.1319',
    ),
    357 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Mandi dan Hukum Junub',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 132',
      'content' => 'dan diterima dari \'Aisyah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam,  "Sesungguhnya aku tidak menghalalkan masjid bagi orang yang sedang haid dan junub."  Riwayat bu Dawud dan hadits shahih menurut Ibnu Khuzaimah.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  إِنِّي لَا أُحِلُّ اَلْمَسْجِدَ لِحَائِضٍ وَلَا جُنُبٌ   رَوَاهُ أَبُو دَاوُدَ وَصَحَّحَهُ اِبْنُ خُزَيْمَة',
      'id' => 'BM.132',
    ),
    358 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1320',
      'content' => 'Abdullah Ibnu Abu Aufa -Semoga Allah meridlainya- berkata: Kami pernah memperoleh makanan pada waktu perang Khaibar. Ada seseorang datang, lalu mengambil sekedarnya, kemudian pergi. Riwayat Abu Dawud. Hadits shahih menurut Ibnu al-Jarud dan Hakim.',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بْنِ أَبِي أَوْفَى رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  أَصَبْنَا طَعَاماً يَوْمَ خَيْبَرَ, فَكَانَ اَلرَّجُلُ يَجِيءُ, فَيَأْخُذُ مِنْهُ مِقْدَارَ مَا يَكْفِيهِ, ثُمَّ يَنْصَرِفُ   أَخْرَجَهُ أَبُو دَاوُدَ, وَصَحَّحَهُ ابْنُ اَلْجَارُودِ, وَالْحَاكِمُ',
      'id' => 'BM.1320',
    ),
    359 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1321',
      'content' => 'Dari Ruwaifi\' Ibnu Tsabit -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa beriman kepada Allah dan hari Akhir, maka janganlah ia mengendarai binatang dan diterima dari harta rampasan kaum muslimin, hingga apabila telah kurus ia kembalikan kepadanya; dan jangan pula ia memakai pakaian dan diterima dari harta rampasan kaum muslimin, hingga apabila telah lusuh ia kembalikan lagi kepadanya." Riwayat Abu Dawud dan Darimy. Para perawinya tidak ada masalah.',
      'arabic' => 'وَعَنْ رُوَيْفِعِ بْنِ ثَابِتٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَنْ كَانَ يُؤْمِنُ بِاَللَّهِ وَالْيَوْمِ اَلْآخِرِ فَلَا يَرْكَبُ دَابَّةً مِنْ فَيْءِ اَلْمُسْلِمِينَ, حَتَّى إِذَا أَعْجَفَهَا رَدَّهَا فِيهِ, وَلَا يَلْبَسُ ثَوْباً مِنْ فَيْءِ اَلْمُسْلِمِينَ حَتَّى إِذَا أَخْلَقَهُ رَدَّهُ فِيهِ   أَخْرَجَهُ أَبُو دَاوُدَ, وَالدَّارِمِيُّ, وَرِجَالُهُ لَا بَأْسَ بِهِمْ',
      'id' => 'BM.1321',
    ),
    360 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1322',
      'content' => 'Abu Ubadah Ibnu al-Jarrah -Semoga Allah meridlainya- berkata: Aku mendengar Rasulullah Shallallaahu \'alaihi wa Sallam bersabda: "Sebagian orang Muslim boleh menanggung keamanan seorang kafir atas nama kaum muslimin." Riwayat Abu Syaibah dan Ahmad dan dalam sanadnya ada kelemahan.',
      'arabic' => 'وَعَنْ أَبِي عُبَيْدَةَ بْنِ اَلْجَرَّاحِ رضي الله عنه قَالَ: سَمِعْتُ رَسُولَ اَللَّهِ صلى الله عليه وسلم يَقُولُ:  يُجِيرُ عَلَى اَلْمُسْلِمِينَ بَعْضُهُمْ   أَخْرَجَهُ ابْنُ أَبِي شَيْبَةَ, وَأَحْمَدُ, وَفِي إِسْنَادِهِ ضَعْفٌ',
      'id' => 'BM.1322',
    ),
    361 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1323',
      'content' => 'Menurut riwayat Thoyalisi dan diterima dari hadits Umar Ibnu al-\'Ash: "Orang muslim yang paling rendah boleh menanggung keamanan seorang kafir atas nama kaum muslimin."',
      'arabic' => 'وَلِلْطَيَالِسِيِّ مِنْ حَدِيثِ عَمْرِوِ بْنِ الْعَاصِ:  يُجِيرُ عَلَى اَلْمُسْلِمِينَ أَدْنَاهُمْ',
      'id' => 'BM.1323',
    ),
    362 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1324',
      'content' => 'Dalam Kitab Shahih Bukhari-Muslim dan diterima dari Ali r.a: "Tanggungan keamanan orang muslim satu, boleh digunakan oleh orang yang paling rendah di antara mereka." Ibnu Majah menambahkan dan diterima dari jalan lain: "Orang muslim yang paling jauh boleh memberi jaminan keamanan atas nama kaum muslimin."',
      'arabic' => 'وَفِي اَلصَّحِيحَيْنِ عَنْ عَلِيٍّ رَضِيَ اَللَّهُ عَنْهُ قَالَ:  ذِمَّةُ اَلْمُسْلِمِينَ وَاحِدَةٌ يَسْعَى ِبهَا أَدْنَاهُمْ  زَادَ اِبْنُ مَاجَه مِنْ وَجْهٍ آخَرَ:   يُجِيرُ عَلَيْهِمْ أَقْصَاهُمْ',
      'id' => 'BM.1324',
    ),
    363 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1325',
      'content' => 'dan dalam (riwayat) ash-Shahihain (Shahih al-Bukhari dan Shahih Muslim) dari hadits yang diterima oleh Ummu Hani\': "Sungguh kami memberi keamanan kepada orang yang engkau beri keamanan."',
      'arabic' => 'وَفِي اَلصَّحِيحَيْنِ مِنْ حَدِيثٍ أَمِ هَانِئٍ:  قَدْ أَجَرْنَا مَنْ أَجَرْتِ',
      'id' => 'BM.1325',
    ),
    364 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1326',
      'content' => 'Dari Umar bahwa ia mendengar Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Aku benar-benar akan mengeluarkan kaum Yahudi dan Nasrani dan diterima dari Jazirah Arab, hingga aku tidak membiarkan kecuali orang muslim." Riwayat Muslim.',
      'arabic' => 'وَعَنْ عُمَرَ رضي الله عنه أَنَّهُ سَمِعَ رَسُولَ اَللَّهِ صلى الله عليه وسلم يَقُولُ:   لَأَخْرِجَنَّ اَلْيَهُودَ وَالنَّصَارَى مِنْ جَزِيرَةِ اَلْعَرَبِ, حَتَّى لَا أَدَعَ إِلَّا مُسْلِماً   رَوَاهُ مُسْلِمٌ',
      'id' => 'BM.1326',
    ),
    365 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1327',
      'content' => 'Dari Umar -Semoga Allah meridlainya- berkata: Adalah harta benda Banu Nadlir merupakan hadta rampasan yang diberikan Allah kepada Rasul-Nya, karena kaum muslimin tidak memranginya dengan kuda maupun kendaraan lainnya. Harta rampasan itu khusus untuk Nabi Shallallaahu \'alaihi wa Sallam yang beliau belanjakan untuk keluarganya selama setahun, dan sisanya dibelikan kuda dan persenjataan perang sebagai persiapan perang di jalan Allah. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْهُ قَالَ:  كَانَتْ أَمْوَالُ بَنِي اَلنَّضِيرِ مِمَّا أَفَاءَ اَللَّهُ عَلَى رَسُولِهِ, مِمَّا لَمْ يُوجِفْ عَلَيْهِ اَلْمُسْلِمُونَ بِخَيْلٍ وَلَا رِكَابٍ, فَكَانَتْ لِلنَّبِيِّ صلى الله عليه وسلم خَاصَّةً, فَكَانَ يُنْفِقُ عَلَى أَهْلِهِ نَفَقَةَ سَنَةٍ, وَمَا بَقِيَ يَجْعَلُهُ فِي اَلْكُرَاعِ وَالسِّلَاحِ, عُدَّةً فِي سَبِيلِ اَللَّهِ   مُتَّفَقٌ عَلَيْه',
      'id' => 'BM.1327',
    ),
    366 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1328',
      'content' => 'Muadz Ibnu Jabal -Semoga Allah meridlainya- berkata: Kami berperang bersama Rasulullah Shallallaahu \'alaihi wa Sallam pada perang Khaibar. Dalam perang itu kami memperoleh kambing-kambing, lalu Rasulullah Shallallaahu \'alaihi wa Sallam membagikan segolongan di antara kami dan sisanya dijadikan sebagai harta rampasan perang. Riwayat Abu Dawud dan para perawinya tidak ada yang cacat.',
      'arabic' => 'وَعَنْ مُعَاذٍ رضي الله عنه قَالَ:  غَزَوْنَا مَعَ رَسُولِ اَللَّهِ صلى الله عليه وسلم خَيْبَرَ, فَأَصَبْنَا فِيهَا غَنَمًا , فَقَسَمَ فِينَا رَسُولُ اَللَّهِ صلى الله عليه وسلم طَائِفَةً, وَجَعَلَ بَقِيَّتَهَا فِي اَلْمَغْنَمِ   رَوَاهُ أَبُو دَاوُدَ, وَرِجَالُهُ لَا بَأْسَ بِهِمْ',
      'id' => 'BM.1328',
    ),
    367 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1329',
      'content' => 'Dari Abu Rafi\' bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Sesungguhnya aku tidak menyalahi janji dan tidak menahan para utusan." Riwayat Abu Dawud dan Nasa\'i. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْ أَبِي رَافِعٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  إِنِّي لَا أَخِيسُ بِالْعَهْدِ, وَلَا أَحْبِسُ اَلرُّسُلَ  رَوَاهُ أَبُو دَاوُدَ, وَالنَّسَائِيُّ, وَصَحَّحَهُ ابْنُ حِبَّانَ',
      'id' => 'BM.1329',
    ),
