    369 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1330',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Negeri manapun yang engkau datangi, lalu engkau berdiam di dalamnya, maka bagianmu berada di dalamnya; dan negeri manapun yang durhaka kepada Allah dan Rasul-Nya, maka seperlima dan diterima dari hasilnya adalah milik Allah dan Rasul-Nya, dan sisanya untukmu." Riwayat Muslim.',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَالَ:  أَيُّمَا قَرْيَةٍ أَتَيْتُمُوهَا, فَأَقَمْتُمْ فِيهَا, فَسَهْمُكُمْ فِيهَا, وَأَيُّمَا قَرْيَةٍ عَصَتْ اَللَّهَ وَرَسُولَهُ, فَإِنْ خُمُسَهَا لِلَّهِ وَرَسُولِهِ , ثُمَّ هِيَ لَكُمْ   رَوَاهُ مُسْلِم',
      'id' => 'BM.1330',
    ),
    370 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1331',
      'content' => 'Dari Abdurrahman Ibnu \'Auf -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam mengambilnya, yaitu upeti, dan diterima dari kaum Majusi Hajar. Riwayat Bukhari. Ada sebuah jalan dalam kitab al-Muwaththo\', namun munqothi\'.',
      'arabic' => 'عَنْ عَبْدِ اَلرَّحْمَنِ بْنِ عَوْفٍ رضي الله عنه  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم أَخَذَهَا - يَعْنِي: اَلْجِزْيَةُ - مِنْ مَجُوسِ هَجَرَ   رَوَاهُ اَلْبُخَارِيّ ُ وَلَهُ طَرِيقٌ فِي اَلْمَوْطَأِ فِيهَا اِنْقِطَاع ٍ',
      'id' => 'BM.1331',
    ),
    371 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1332',
      'content' => 'Dari Ashim Ibnu Umar, dan diterima dari Anas, dan diterima dari Utsman Ibnu Abu Sulaiman, -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam mengirimkan Kholid Ibnu Walid untuk menangkap Ukaidir dan diterima dari Dumatul Jandal. Lalu mereka Kholid dan tentaranya menangkapnya dan membawanya kepada beliau. Beliau menyelamatkan jiwanya dan berdamai dengannya dengan membayar upeti. Riwayat Abu Dawud.',
      'arabic' => 'وَعَنْ عَاصِمِ بْنِ عُمَرَ, عَنْ أَنَسٍ, وَعَن ْ عُثْمَانَ بْنِ أَبِي سُلَيْمَانَ;  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم بَعْثٍ خَالِدُ بْنُ اَلْوَلِيدِ إِلَى أُكَيْدِرِ دُومَةَ, فَأَخَذُوهُ , فَحَقَنَ دَمِهِ, وَصَالَحَهُ عَلَى اَلْجِزْيَةِ   رَوَاهُ أَبُو دَاوُد',
      'id' => 'BM.1332',
    ),
    372 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1333',
      'content' => 'Muadz Ibnu Jabal -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam pernah mengutusku ke negeri Yaman. Beliau memerintahkan kepadaku agar mengambil dan diterima dari setiap orang dewasa satu dinar atau senilai satu dinar dan diterima dari kain mu\'afiry. Riwayat Imam Tiga. Hadits shahih menurut Ibnu Hibban dna Hakim.',
      'arabic' => 'وَعَنْ مُعَاذِ بْنِ جَبَلٍ رضي الله عنه قَالَ:  بَعَثَنِي اَلنَّبِيُّ صلى الله عليه وسلم إِلَى اَلْيَمَنِ, وَأَمَرَنِي أَنْ آخُذَ مِنْ كُلِّ حَالِمٍ دِينَاراً, أَوْ عَدْلَهُ معافرياً   أَخْرَجَهُ اَلثَّلَاثَةِ, وَصَحَّحَهُ اِبْنُ حِبَّانَ, وَالْحَاكِم',
      'id' => 'BM.1333',
    ),
    373 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1334',
      'content' => 'Dari \'Aidz Ibnu Umar dan al-Muzanny -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Islam itu tinggi dan tidak ada yang mengalahkan ketinggiannya." Riwayat Daruquthni.',
      'arabic' => 'وَعَنْ عَائِذٍ بْنُ عَمْرِوِ الْمُزَنِيِّ رضي الله عنه عَنْ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  اَلْإِسْلَامِ يَعْلُو, وَلَا يُعْلَى   أَخْرَجَهُ اَلدَّارَقُطْنِيّ ُ',
      'id' => 'BM.1334',
    ),
    374 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1335',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Jangan mendahului orang Yahudi dan Nasrani dengan ucapan salam, bila kalian bertemu dengan seorang di antara mereka usahakan ia mendapat jalan yang paling sempit." Riwayat Muslim.',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَالَ:   لَا تَبْدَؤُوا اَلْيَهُودَ وَالنَّصَارَى بِالسَّلَامِ, وَإِذَا لَقِيتُمْ أَحَدَهُمْ فِي طَرِيقٍ, فَاضْطَرُّوهُ إِلَى أَضْيَقِهِ   رَوَاهُ مُسْلِم ٌ',
      'id' => 'BM.1335',
    ),
    375 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1336',
      'content' => 'Dari al-Miswar Ibnu Makhramah dan Marwan bahwa Nabi Shallallaahu \'alaihi wa Sallam keluar pada tahun Hudaibiyah. Perawi menyebutkan hadits dengan panjang dan di dalamnya disebutkan: Inilah perjanjian perdamaian yang dibuat Muhammad Ibnu Abdullah kepada Suhail Ibnu Amar, yaitu menghentikan peperangan selama sepuluh tahun untuk menjamin keamanan manusia dan tidak boleh saling menyerang. Riwayat Abu Dawud dan asalnya dalam riwayat Bukhari.',
      'arabic' => 'وَعَنْ اَلْمِسْوَرِ بْنُ مَخْرَمَةَ. وَمَرْوَانُ;  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم خَرَجَ عَامَ اَلْحُدَيْبِيَةِ. فَذَكِّرْ اَلْحَدِيثَ بِطُولِهِ, وَفِيهِ هَذَا مَا صَالَحَ عَلَيْهِ مُحَمَّدُ بْنُ عَبْدِ اَللَّهِ سُهَيْلِ بْنِ عَمْرِوٍ عَلَى وَضْعِ اَلْحَرْبِ عَشْرِ سِنِينَ, يَأْمَنُ فِيهَا اَلنَّاسُ, وَيَكُفُّ بَعْضُهُمْ عَنْ بَعْضِ   أَخْرَجَهُ أَبُو دَاوُد َ وَأَصْلِهِ فِي اَلْبُخَارِيّ',
      'id' => 'BM.1336',
    ),
    376 => 
    array (
      'chapter' => 'Kitab al-Jihad (Jihad): Bab Upeti dan Gencatan Senjata',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1337',
      'content' => 'Muslim meriwayatkan sebagian hadits tersebut dan diterima dari Anas -Semoga Allah meridlainya- dan di dalamnya disebutkan: Bahwa barangsiapa datang kepada kami kaum kafir dan diterima dari pihakmu tidak akan kami kembalikan kepadamu dan barangsiapa datang kepadamu kaum muslim dan diterima dari pihak kami, akan engkau kembalikan kepada kami. Maka para sahabat bertanya: Apakah baginda menulis ini, wahai Rasulullah? Beliau bersabda: "Ya, karena barangsiapa di antara kita yang pergi kepada mereka Allah akan menjauhkan darinya dan barangsiapa di antara mereka datang kepada kita Allah akan menjadikan untuknya kelonggaran dan jalan keluar."',
      'arabic' => 'وَأَخْرُجَ مُسْلِمٍ بَعْضِهِ مِنْ حَدِيثِ أَنَسٍ, وَفِيهِ:  أَنَّ مَنْ جَاءَ مِنْكُمْ لَمْ نَرُدْهُ عَلَيْكُمْ, وَمَنْ جَاءَكُمْ مِنَّا رَدَدْتُمُوهُ عَلَيْنَا. فَقَالُوا: أَنَكْتُبُ هَذَا يَا رَسُولُ اَللَّهُ? قَالَ: نَعَمْ. إِنَّهُ مِنْ ذَهَبٍ مِنَّا إِلَيْهِمْ فَأَبْعَدَهُ اَللَّهُ, وَمَنْ جَاءَنَا مِنْهُمْ, فَسَيَجْعَلُ اَللَّهُ لَهُ فَرَجاً وَمُخْرِجاً',
      'id' => 'BM.1337',
    ),
    377 => 
