    ),
    396 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1355',
      'content' => 'Ibnu Abbas -Semoga Allah meridlainya- berkata: Biawak pernah dimakan oleh para shahabat dalam hidangan Rasulullah Shallallaahu \'alaihi wa Sallam (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  أَكُلَّ اَلضَّبِّ عَلَى مَائِدَةِ رَسُولُ اَللَّهِ صلى الله عليه وسلم   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1355',
    ),
    397 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1356',
      'content' => 'Dari Abdurrahman Ibnu Utsman al-Qurasyi -Semoga Allah meridlainya- bahwa ada seorang thabib dokter bertanya kepada Rasulullah Shallallaahu \'alaihi wa Sallam tentang katak yang dijadikan obat. Lalu beliau melarang membunuhnya. Riwayat Ahmad yang dinilai shahih oleh Hakim. Abu Dawud dan Nasa\'i juga meriwayatkannya.',
      'arabic' => 'وَعَنْ عَبْدِ اَلرَّحْمَنِ بْنِ عُثْمَانَ اَلْقُرَشِيُّ رضي الله عنه  أَنَّ طَبِيباً سَأَلَ رَسُولَ اَللَّهِ صلى الله عليه وسلم صلى الله عليه وسلم عَنْ اَلضِّفْدَعِ يَجْعَلُهَا فِي دَوَاءٍ, فَنَهَى عَنْ قَتْلِهَا   أَخْرَجَهُ أَحْمَدُ, وَصَحَّحَهُ اَلْحَاكِمُ',
      'id' => 'BM.1356',
    ),
    398 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1357',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa memelihara anjing -kecuali anjing penjaga ternak, anjing pemburu, atau anjing penjaga tanaman- pahalanya akan dikurangi satu qirath setiap hari." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'عَنْ أَبِي هُرَيْرَةَ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَنِ اتَّخَذَ كَلْباً, إِلَّا كَلْبَ مَاشِيَةٍ, أَوْ صَيْدٍ, أَوْ زَرْعٍ, اِنْتَقَصَ مِنْ أَجْرِهِ كُلَّ يَوْمٍ قِيرَاطٌ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1357',
    ),
    399 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1358',
      'content' => 'Dari \'Adiy Ibnu Hatim -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Jika engkau melepaskan anjingmu untuk berburu, maka sebutlah nama Allah padanya. Bila ia menangkap buruan untukmu dan engkau mendapatkannya masih hidup, maka sembelihlah. Bila engkau mendapatkannya telah mati dan anjing itu tidak memakannya sama sekali, maka makanlah. Bila engkau menemukan anjing lain selain anjingmu, sedang buruan itu telah mati, maka jangan engkau makan sebab engkau tidak mengetahui anjing mana yang membunuhnya. Apabila engkau melepaskan panahmu, sebutlah nama Allah. Bila engkau baru menemukan buruan itu setelah sehari dan tidak engkau temukan selain bekas panahmu, makanlah jika engkau mau. Jika engkau menemukannya tenggelam di dalam air, janganlah engkau memakannya." (HR. Muttafaqun \'Alaih dan lafazh (hadits) ini menurut Muslim).',
      'arabic' => 'وَعَنْ عَدِيِّ بنِ حَاتِمٍ رضي الله عنه قَالَ: قَالَ لِي رَسُولُ اَللَّهِ صلى الله عليه وسلم  إِذَا أَرْسَلَتَ كَلْبَكَ فَاذْكُرِ اسْمَ اَللَّهِ, فَإِنْ أَمْسَكَ عَلَيْكَ فَأَدْرَكْتَهُ حَيًّا فَاذْبَحْهُ, وَإِنْ أَدْرَكْتَهُ قَدْ قُتِلَ وَلَمْ يُؤْكَلْ مِنْهُ فَكُلْهُ, وَإِنْ وَجَدْتَ مَعَ كَلْبِكَ كَلْبًا غَيْرَهُ وَقَدْ قُتِلَ فَلَا تَأْكُلْ: فَإِنَّكَ لَا تَدْرِي أَيَّهُمَا قَتَلَهُ, وَإِنْ رَمَيْتَ سَهْمَكَ فَاذْكُرِ اسْمَ اَللَّهِ, فَإِنْ غَابَ عَنْكَ يَوْماً, فَلَمْ تَجِدْ فِيهِ إِلَّا أَثَرَ سَهْمِكَ, فَكُلْ إِنْ شِئْتَ, وَإِنْ وَجَدْتَهُ غَرِيقاً فِي اَلْمَاءِ, فَلَا تَأْكُلْ   مُتَّفَقٌ عَلَيْهِ, وَهَذَا لَفْظُ مُسْلِمٍ',
      'id' => 'BM.1358',
    ),
    400 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1359',
      'content' => '\'Ady -Semoga Allah meridlainya- berkata: Aku bertanya kepada Rasulullah Shallallaahu \'alaihi wa Sallam tentang berburu dengan tombak. Beliau bersabda: "Jika engkau mengenakan dengan ujungnya yang tajam, makanlah; dan jika engkau mengenakan dengan tangkainya, kemudian ia terbunuh, maka ia adalah mati terkena pukulan dan jangan dimakan." Riwayat Bukhari.',
      'arabic' => 'وَعَنْ عَدِيٍّ قَالَ:  سَأَلْتُ رَسُولَ اَللَّهِ صلى الله عليه وسلم عَنْ صَيْدِ اَلْمِعْرَاضِ فَقَالَ: إِذَا أَصَبْتَ بِحَدِّهِ فَكُلْ, وَإِذَا أَصَبْتَ بِعَرْضِهِ, فَقُتِلَ, فَإِنَّهُ وَقِيذٌ, فَلَا تَأْكُلْ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.1359',
    ),
    401 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Tayammum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 136',
      'content' => 'diterima dari Jabir Ibnu Abdullah bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda:  "Aku diberi lima hal yang belum pernah diberikan kepad seorang pun sebelumku yaitu aku ditolong dengan rasa ketakutan musuhku sejauh perjalanan sebulan; bumi dijadikan untukku sebagai tempat sujud masjid dan alat bersuci maka siapapun menemui waktu shalat hendaklah ia segera shalat."  (HR. Muttafaqun \'Alaih).',
      'arabic' => 'عَنْ جَابِرِ بْنِ عَبْدِ اَللَّهِ رَضِيَ اَللَّهُ عَنْهُمَا; أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:  أُعْطِيتُ خَمْسًا لَمْ يُعْطَهُنَّ أَحَدٌ قَبْلِي: نُصِرْتُ بِالرُّعْبِ مَسِيرَةَ شَهْرٍ وَجُعِلَتْ لِي اَلْأَرْضُ مَسْجِدًا وَطَهُورًا فَأَيُّمَا رَجُلٍ أَدْرَكَتْهُ اَلصَّلَاةُ فَلْيُصَلِّ   وَذَكَرَ اَلْحَدِيث',
      'id' => 'BM.136',
    ),
    402 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1360',
      'content' => 'Dari Abu Tsa\'labah bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Jika engkau melepaskan panahmu, lalu buruan itu menghilang darimu, kemudian engkau temukan, maka makanlah selama ia belum membusuk." Riwayat Muslim.',
      'arabic' => 'وَعَنْ أَبِي ثَعْلَبَةَ رضي الله عنه عَنِ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  إِذَا رَمَيْتَ بِسَهْمِكَ, فَغَابَ عَنْكَ, فَأَدْرَكْتَهُ فَكُلْهُ, مَا لَمْ يُنْتِنْ   أَخْرَجَهُ مُسْلِمٌ',
      'id' => 'BM.1360',
    ),
    403 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1361',
      'content' => 'Dari \'Aisyah -Semoga Allah meridlainya- bahwa ada suatu kaum bertanya kepada Nabi Shallallaahu \'alaihi wa Sallam: Ada suatu kaum membawa daging kepada kami yang tidak kami ketahui, apakah mereka menyebut nama Allah waktu menyembelih atau tidak?. Beliau menjawab: "Sebutlah nama Allah padanya dan makanlah." Riwayat Bukhari.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا;  أَنَّ قَوْمًا قَالُوا لِلنَّبِيِّ صلى الله عليه وسلم إِنَّ قَوْماً يَأْتُونَنَا بِاللَّحْمِ, لَا نَدْرِي أَذُكِرَ اِسْمُ اَللَّهِ عَلَيْهِ أَمْ لَا? فَقَالَ: سَمُّوا اَللَّهَ عَلَيْهِ أَنْتُمْ, وَكُلُوهُ  رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.1361',
    ),
    404 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1362',
      'content' => 'Dari Abdullah Ibnu Mughoffal -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam melarang berburu dengan cara melempar batu. Beliau bersabda: "Ia tidak dapat memburu buruan, tidak menyakiti musuh, ia hanya meretakkan gigi dan membutakan mata." (HR. Muttafaqun \'Alaih dan lafadznya menurut riwayat Muslim).',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بنِ مُغَفَّلٍ الْمُزَنِيِّ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم نَهَى عَنِ اَلْخَذْفِ, وَقَالَ: إِنَّهَا لَا تَصِيدُ صَيْدًا, وَلَا تَنْكَأُ عَدُوًّا, وَلَكِنَّهَا تَكْسِرُ اَلسِّنَّ, وَتَفْقَأُ اَلْعَيْنَ   مُتَّفَقٌ عَلَيْهِ. وَاللَّفْظُ لِمُسْلِمٍ',
      'id' => 'BM.1362',
    ),
    405 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1363',
      'content' => 'Dari Ibnu Abbas -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Janganlah engkau jadikan sesuatu yang berjiwa itu sebagai sasaran." Riwayat Muslim.',
      'arabic' => 'وَعَنِ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا; أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:  لَا تَتَّخِذُوا شَيْئاً فِيهِ اَلرُّوحُ غَرَضًا   رَوَاهُ مُسْلِمٌ',
