    407 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1365',
      'content' => 'Dari Rafi\' Ibnu Khodij -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Apa yang dapat menumpahkan darah dengan diiringi sebutan nama Allah, makanlah, selain gigi dan kuku, sebab gigi adalah tulang sedang kuku adalah pisau bangsa Habasyah." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ رَافِعِ بْنِ خَدِيجٍ رضي الله عنه عَنِ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  مَا أُنْهِرَ اَلدَّمُ, وَذُكِرَ اِسْمُ اَللَّهِ عَلَيْهِ, فَكُلْ لَيْسَ اَلسِّنَّ وَالظُّفْرَ; أَمَّا اَلسِّنُّ; فَعَظْمٌ; وَأَمَّا اَلظُّفُرُ: فَمُدَى اَلْحَبَشِ   مُتَّفَقٌ عَلَيْه',
      'id' => 'BM.1365',
    ),
    408 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1366',
      'content' => 'Jabir Ibnu Abdullah -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam melarang membunuh suatu binatang dengan cara mengikatnya lalu memanahnya. Riwayat Muslim.',
      'arabic' => 'وَعَنْ جَابِرِ بنِ عَبْدِ اَللَّهِ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  نَهَى رَسُولُ اَللَّهِ صلى الله عليه وسلم أَنْ يُقْتَلَ شَيْءٌ مِنَ اَلدَّوَابِّ صَبْرًا   رَوَاهُ مُسْلِمٌ',
      'id' => 'BM.1366',
    ),
    409 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1367',
      'content' => 'Dari Syaddad Ibnu Aus ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Sesungguhnya Allah mewajibkan berbuat kebaikan terhadap segala sesuatu. Maka jika engkau membunuh, bunuhlah dengan cara yang baik dan jika engkau menyembelih, sembelihlah dengan cara yang baik, dan hendaklah di antara kamu mempertajam pisaunya dan memudahkan kematian binatang sembelihannya." Riwayat Muslim.',
      'arabic' => 'وَعَنْ شَدَّادِ بْنِ أَوْسٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم   إِنَّ اَللَّهَ كَتَبَ اَلْإِحْسَانَ عَلَى كُلِّ شَيْءٍ, فَإِذَا قَتَلْتُمْ فَأَحْسِنُوا اَلْقِتْلَةَ, وَإِذَا ذَبَحْتُمْ فَأَحْسِنُوا اَلذِّبْحَةَ, وَلْيُحِدَّ أَحَدُكُمْ شَفْرَتَهُ, وَلْيُرِحْ ذَبِيحَتَهُ   رَوَاهُ مُسْلِمٌ',
      'id' => 'BM.1367',
    ),
    410 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1368',
      'content' => 'Dari Abu Said al-Khudry -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Menyembelih membunuh janin adalah menyembelih ibunya." Riwayat Ahmad. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْ أَبِي سَعِيدٍ اَلْخُدْرِيِّ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  ذَكَاةُ اَلْجَنِينِ ذَكَاةُ أُمِّهِ   رَوَاهُ أَحْمَدُ, وَصَحَّحَهُ اِبْنُ حِبَّانَ',
      'id' => 'BM.1368',
    ),
    411 => 
    array (
      'chapter' => 'Kitab al-Athi\'mah (Makanan): Bab Binatang Buruan dan Sembelihan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1369',
      'content' => 'Dari Ibnu Abbas -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Orang muslim itu cukup dengan namanya. Bila ia lupa menyebut nama Allah ketika menyembelih, hendaknya ia menyebut nama Allah sebelum makan, kemudian memakannya." Riwayat Daruquthni dan dalam sanadnya ada seorang perawi yang lemah hafalannya, bernama Muhammad Ibnu Yazid Ibnu Sinad. Ia seorang yang jujur, namun lemah hafalannya.',
      'arabic' => 'وَعَنِ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا; أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:   اَلْمُسْلِمُ يَكْفِيهِ اِسْمُهُ, فَإِنْ نَسِيَ أَنْ يُسَمِّيَ حِينَ يَذْبَحُ, فَلْيُسَمِّ, ثُمَّ لِيَأْكُلْ   أَخْرَجَهُ اَلدَّارَقُطْنِيُّ, وَفِي إِسْنَادِهِ مُحَمَّدُ بنُ يَزِيدَ بنِ سِنَانٍ, وَهُوَ صَدُوقٌ ضَعِيفُ اَلْحِفْظ',
      'id' => 'BM.1369',
    ),
    412 => 
    array (
