    430 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1386',
      'content' => 'Dari Ibnu Umar -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam menjumpai Umar Ibnu Al-Khaththab di suatu kafilah, padahal ia Umar sedang bersumpah dengan nama ayahnya. Maka Rasulullah Shallallaahu \'alaihi wa Sallam berseru kepada mereka: "Ketahuilah bahwa Allah melarang kalian untuk bersumpah dengan nama ayahmu. Barangsiapa bersumpah, hendaknya bersumpah dengan nama Allah atau diam." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'عَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا,  عَنْ رَسُولِ اَللَّهِ صلى الله عليه وسلم أَنَّهُ أَدْرَكَ عُمَرَ بْنَ اَلْخَطَّابِ فِي رَكْبٍ, وَعُمَرَ يَحْلِفُ بِأَبِيهِ, فَنَادَاهُمْ رَسُولُ اَللَّهِ صلى الله عليه وسلم أَلَا إِنَّ اَللَّهَ يَنْهَاكُمْ أَنْ تَحْلِفُوا بِآبَائِكُمْ, فَمَنْ كَانَ حَالِفاً فَلْيَحْلِفْ بِاَللَّهِ, أَوْ لِيَصْمُتْ مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.1386',
    ),
    431 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1387',
      'content' => 'Menurut suatu riwayat Abu Dawud dan Nasa\'i dan diterima dari Abu Hurairah -Semoga Allah meridlainya- dalam hadits marfu\': Jangan bersumpah dengan nama ayahmu, ibumu, dan apa-apa yang disekutukan dengan Allah. Dan jangan bersumpah dengan nama Allah, kecuali kalian dalam keadaan benar."',
      'arabic' => 'وَفِي رِوَايَةٍ لِأَبِي دَاوُدَ, وَالنَّسَائِيِّ: عَنْ أَبِي هُرَيْرَةَ رضي الله عنه  لَا تَحْلِفُوا بِآبَائِكُمْ, وَلَا بِأُمَّهَاتِكُمْ, وَلَا بِالْأَنْدَادِ, وَلَا تَحْلِفُوا إِلَّا بِاَللَّهِ, وَلَا تَحْلِفُوا بِاَللَّهِ إِلَّا وَأَنْتُمْ صَادِقُونَ',
      'id' => 'BM.1387',
    ),
    432 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1388',
