    434 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Tayammum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 139',
      'content' => 'Ammar Ibnu Yassir -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam telah mengutusku untuk suatu keperluan lalu aku junub dan tidak mendapatkan air maka aku bergulingan di atas tanah seperti yang dilakukan binatang kemudian aku mendatangi Nabi Shallallaahu \'alaihi wa Sallam dan menceritakan hal itu padanya. Rasulullah Shallallaahu \'alaihi wa Sallam bersabda: "sesungguhnya engkau cukup degnan kedua belah tanganmu begini." Lalu beliau menepuk tanah sekali kemudian mengusapkan tangan kirinya atas tangan kanannya punggung kedua telapak tangan dan wajahnya. (HR. Muttafaqun \'Alaih dan lafazhnya menurut riwayat Muslim)',
      'arabic' => 'وَعَنْ عَمَّارِ بْنِ يَاسِرٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  بَعَثَنِي اَلنَّبِيُّ صلى الله عليه وسلم فِي حَاجَةٍ فَأَجْنَبْتُ فَلَمْ أَجِدِ اَلْمَاءَ فَتَمَرَّغْتُ فِي اَلصَّعِيدِ كَمَا تَمَرَّغُ اَلدَّابَّةُ ثُمَّ أَتَيْتُ اَلنَّبِيَّ صلى الله عليه وسلم فَذَكَرْتُ ذَلِكَ لَهُ فَقَالَ: إِنَّمَا كَانَ يَكْفِيكَ أَنْ تَقُولَ بِيَدَيْكَ هَكَذَا ثُمَّ ضَرَبَ بِيَدَيْهِ اَلْأَرْضَ ضَرْبَةً وَاحِدَةً ثُمَّ مَسَحَ اَلشِّمَالَ عَلَى اَلْيَمِينِ وَظَاهِرَ كَفَّيْهِ وَوَجْهَهُ   مُتَّفَقٌ عَلَيْهِ وَاللَّفْظُ لِمُسْلِم',
      'id' => 'BM.139',
    ),
    435 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1390',
      'content' => 'Dari Abdurrahman Ibnu Samurah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Apabila engkau bersumpah terhadap suatu hal, lalu engkau melihat ada sesuatu yang lebih baik daripada sumpahmu, maka bayarlah kafarat untuk sumpahmu dan lakukan hal yang lebih baik itu." (HR. Muttafaqun \'Alaih). Dan dalam lafadz riwayat al-Bukhari "Lakukan hal yang lebih baik itu dan bayarlah kafarat sumpahmu." Dan dalam riwayat Abu Dawud: "Bayarlah kafarat sumpahmu, kemudian lakukan apa yang lebih baik itu. Sanad kedua hadits tersebut shahih.',
      'arabic' => 'وَعَنْ عَبْدِ اَلرَّحْمَنِ بْنِ سَمُرَةٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  وَإِذَا حَلَفْتَ عَلَى يَمِينٍ, فَرَأَيْتُ غَيْرَهَا خَيْراً مِنْهَا, فَكَفِّرْ عَنْ يَمِينِكَ, وَائْتِ اَلَّذِي هُوَ خَيْرٌ   مُتَّفَقٌ عَلَيْهِ وَفِي لَفْظٍ لِلْبُخَارِيِّ  فَائِت اَلَّذِي هُوَ خَيْرٌ, وَكَفِّرْ عَنْ يَمِينِكَ  وَفِي رِوَايَةٍ لِأَبِي دَاوُدَ  فَكَفِّرْ عَنْ يَمِينِكَ, ثُمَّ اِئْتِ اَلَّذِي هُوَ خَيْرٌ   وَإِسْنَادُهَا صَحِيحٌ',
      'id' => 'BM.1390',
    ),
    436 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1391',
      'content' => 'Dari Ibnu Umar -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa bersumpah atas suatu hal, lalu ia mengucapkan insyaAllah jika Allah menghendaki, tidak ada denda atasnya jika ia melanggarnya." Riwayat Ahmad dan Imam Empat. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَالَ:  مَنْ حَلِفِ عَلَى يَمِينٍ فَقَالَ: إِنْ شَاءَ اَللَّهُ, فَلَا حِنْثَ عَلَيْهِ   رَوَاهُ اَلْخَمْسَةُ. وَصَحَّحَهُ اِبْنُ حِبَّانَ',
      'id' => 'BM.1391',
    ),
    437 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1392',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Sumpah Nabi Shallallaahu \'alaihi wa Sallam ialah: Tidak, demi yang membalikkan hati." Riwayat Bukhari.',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  كَانَتْ يَمِينُ اَلنَّبِيِّ صلى الله عليه وسلم لَا, وَمُقَلِّبِ اَلْقُلُوبِ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.1392',
    ),
    438 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1393',
      'content' => 'Abdullah Ibnu Umar -Semoga Allah meridlainya- berkata: Seorang Arab Badui menemui Nabi Shallallaahu \'alaihi wa Sallam dan bertanya: Wahai Rasulullah, apakah dosa-dosa besar itu? -perawi melanjutkan hadits dan di dalamnya disebutkan- "Sumpah palsu." Dalam hadits itu aku bertanya: Apa itu sumpah palsu? Beliau bersabda: "Sumpah yang digunakan untuk mengambil harta orang muslim, padahal ia bohong." Riwayat Muslim.',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بْنِ عَمْرِوٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  جَاءَ أَعْرَابِيٌّ إِلَى اَلنَّبِيِّ صلى الله عليه وسلم فَقَالَ: يَا رَسُولَ اَللَّهِ! مَا اَلْكَبَائِرُ? فَذَكَرَ اَلْحَدِيثَ, وَفِيهِ قُلْتُ: وَمَا اَلْيَمِينُ اَلْغَمُوسُ? قَالَ: اَلَّذِي يَقْتَطِعُ مَالَ امْرِئٍ مُسْلِمٍ, هُوَ فِيهَا كَاذِبٌ   أَخْرَجَهُ اَلْبُخَارِيُّ',
      'id' => 'BM.1393',
    ),
    439 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1394',
      'content' => 'Dari \'Aisyah -Semoga Allah meridlainya- tentang firman-Nya artinya = Allah tidak akan menuntut sumpah-sumpahmu yang tidak disengaja, ia berkata: Itu adalah perkataan orang: Tidak, demi Allah dan benar, demi Allah." Riwayat Bukhari. Abu Dawud meriwayatkannya dengan marfu\'.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا  فِي قَوْلِهِ تَعَالَى: لَا يُؤَاخِذُكُمُ اَللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ قَالَتْ: هُوَ قَوْلُ اَلرَّجُلِ: لَا وَاَللَّهِ بَلَى وَاَللَّهِ   أَخْرَجَهُ اَلْبُخَارِيُّ  وَأَوْرَدَهُ أَبُو دَاوُدَ مَرْفُوعاً',
      'id' => 'BM.1394',
    ),
    440 => 
    array (
      'chapter' => 'Kitab al-Aimaan wa an-Nudzuur (Sumpah dan Nazar)',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 1395',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Sesungguhnya Allah mempunyai 99 nama, barangsiapa menghafalnya ia masuk surga." (HR. Muttafaqun \'Alaih). Telah menguraikan at-Tirmidzi dan Ibnu Hibban mengenai nama-nama tersebut, sebenarnya penyebutan nama-nama itu adalah penyusupan oleh sebagian perawi hadits.',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  إِنَّ لِلَّهِ تِسْعًا وَتِسْعِينَ اِسْماً, مَنْ أَحْصَاهَا دَخَلَ اَلْجَنَّةَ   مُتَّفَقٌ عَلَيْهِ وَسَاقَ اَلتِّرْمِذِيُّ وَابْنُ حِبَّانَ اَلْأَسْمَاءِ, وَالتَّحْقِيقُ أَنَّ سَرْدَهَا إِدْرَاجٌ مِنْ بَعْضِ اَلرُّوَاةِ',
      'id' => 'BM.1395',
    ),
    441 => 
