    1242 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 680',
      'content' => 'Dari Sulaiman Ibnu Amir Al-Dlobby bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Apabila seseorang di antara kamu berbuka, hendaknya ia berbuka dengan kurma, jika tidak mendapatkannya, hendaknya ia berbuka dengan air karena air itu suci." Riwayat Imam Lima. Hadits shahih menurut Ibnu Khuzaimah, Ibnu Hibban, dan Hakim.',
      'arabic' => 'وَعَنْ سَلْمَانَ بْنِ عَامِرٍ اَلضَّبِّيِّ رضي الله عنه عَنِ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  إِذَا أَفْطَرَ أَحَدُكُمْ فَلْيُفْطِرْ عَلَى تَمْرٍ, فَإِنْ لَمْ يَجِدْ فَلْيُفْطِرْ عَلَى مَاءٍ, فَإِنَّهُ طَهُورٌ   رَوَاهُ اَلْخَمْسَةُ, وَصَحَّحَهُ اِبْنُ خُزَيْمَةَ وَابْنُ حِبَّانَ وَالْحَاكِمُ',
      'id' => 'BM.680',
    ),
    1243 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 681',
      'content' => 'Abu Hurairah -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam melarang puasa wishol puasa bersambung tanpa makan. Lalu ada seorang dan diterima dari kaum muslimin bertanya: Tetapi baginda sendiri puasa wishol, wahai Rasulullah? Beliau menjawab: "Siapa di antara kamu yang seperti aku, aku bermalam dan Tuhanku memberi makan dan minum." Karena mereka menolak untuk berhenti puasa wishol, maka beliau shaum wishol bersama mereka sehari, kemudian sehari. Lalu mereka melihat bulan sabit, maka bersabdalah beliau: "Seandainya bulan sabit tertunda aku akan tambahkan puasa wishol untukmu, sebagai pelajaran bagi mereka uang menolak untuk berhenti." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه قَالَ:  نَهَى رَسُولُ اَللَّهِ صلى الله عليه وسلم عَنِ اَلْوِصَالِ، فَقَالَ رَجُلٌ مِنَ اَلْمُسْلِمِينَ: فَإِنَّكَ يَا رَسُولَ اَللَّهِ تُوَاصِلُ؟ قَالَ: وَأَيُّكُمْ مِثْلِي؟ إِنِّي أَبِيتُ يُطْعِمُنِي رَبِّي وَيَسْقِينِي فَلَمَّا أَبَوْا أَنْ يَنْتَهُوا عَنِ اَلْوِصَالِ وَاصَلَ بِهِمْ يَوْمًا، ثُمَّ يَوْمًا، ثُمَّ رَأَوُا اَلْهِلَالَ، فَقَالَ: لَوْ تَأَخَّرَ اَلْهِلَالُ لَزِدْتُكُمْ كَالْمُنَكِّلِ لَهُمْ حِينَ أَبَوْا أَنْ يَنْتَهُوا   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.681',
    ),
    1244 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 682',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa tidak meninggalkan perkataan dusta dan mengerjakannya serta berlaku bodoh, maka tidak ada keperluan bagi Allah untuk meninggalkan makanan dan minumannya." Riwayat Bukhari dan Abu Dawud. Lafadznya menurut riwayat Abu Dawud.',
      'arabic' => 'وَعَنْهُ قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَنْ لَمْ يَدَعْ قَوْلَ اَلزُّورِ وَالْعَمَلَ بِهِ, وَالْجَهْلَ, فَلَيْسَ لِلَّهِ حَاجَةٌ فِي أَنْ يَدَعَ طَعَامَهُ وَشَرَابَهُ   رَوَاهُ اَلْبُخَارِيُّ, وَأَبُو دَاوُدَ وَاللَّفْظُ لَه',
      'id' => 'BM.682',
    ),
    1245 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 683',
      'content' => '\'Aisyah -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam pernah mencium sewaktu shaum dan mencumbu sewaku shaum, akan tetapi beliau adalah orang yang paling kuat menahan nafsunya di antara kamu. (HR. Muttafaqun \'Alaih dan lafazhnya menurut Muslim). Dan dalam suatu riwayat ditambahkan: Pada bulan Ramadhan.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  كَانَ رَسُولُ اَللَّهِ صلى الله عليه وسلم يُقَبِّلُ وَهُوَ صَائِمٌ, وَيُبَاشِرُ وَهُوَ صَائِمٌ, وَلَكِنَّهُ أَمْلَكُكُمْ لِإِرْبِهِ   مُتَّفَقٌ عَلَيْهِ, وَاللَّفْظُ لِمُسْلِمٍ. وَزَادَ فِي رِوَايَةٍ:  فِي رَمَضَانَ',
      'id' => 'BM.683',
    ),
    1246 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 684',
      'content' => 'Dari Ibnu Abbas -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam pernah berbekam dalam keadaan ihram dan pernah berbekam sewaktu shaum. Riwayat Bukhari.',
      'arabic' => 'وَعَنِ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا;  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم اِحْتَجَمَ وَهُوَ مُحْرِمٌ, وَاحْتَجَمَ وَهُوَ صَائِمٌ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.684',
    ),
    1247 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 685',
      'content' => 'Dari Syaddad Ibnu Aus bahwa Nabi Shallallaahu \'alaihi wa Sallam pernah melewati seseorang yang sedang berbekam pada bulan Ramadhan di Baqi\', lalu beliau bersabda: "Batallah puasa orang yang membekam dan dibekam." Riwayat Imam Lima kecuali Tirmidzi. Hadits shahih menurut Ahmad, Ibnu Khuzaimah, dan Ibnu Hibban.',
      'arabic' => 'وَعَنْ شَدَّادِ بْنِ أَوْسٍ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم أَتَى عَلَى رَجُلٍ بِالْبَقِيعِ وَهُوَ يَحْتَجِمُ فِي رَمَضَانَ. فَقَالَ:  أَفْطَرَ اَلْحَاجِمُ  ]وَالْمَحْجُومُ [    رَوَاهُ اَلْخَمْسَةُ إِلَّا اَلتِّرْمِذِيَّ, وَصَحَّحَهُ أَحْمَدُ, وَابْنُ خُزَيْمَةَ, وَابْنُ حِبَّانَ',
      'id' => 'BM.685',
    ),
    1248 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 687',
      'content' => 'Anas Ibnu Malik -Semoga Allah meridlainya- berkata: Pertama kali pembekaman bagi orang yang puasa itu dimakruhkan adalah ketika Ja\'far Ibnu Abu Thalib berbekam sewaktu shaum. Lalu Nabi Shallallaahu \'alaihi wa Sallam melewatinya dan beliau bersabda: "Batallah dua orang ini." Setelah itu Nabi Shallallaahu \'alaihi wa Sallam memberikan keringanan untuk berbekam bagi orang yang shaum. Dan Anas pernah berbekam ketika shaum. Riwayat Daruquthni dan ia menguatkannya.',
      'arabic' => 'وَعَنْ أَنَسِ بْنِ مَالِكٍ رضي الله عنه قَالَ:  أَوَّلُ مَا كُرِهَتِ اَلْحِجَامَةُ لِلصَّائِمِ; أَنَّ جَعْفَرَ بْنَ أَبِي طَالِبٍ اِحْتَجَمَ وَهُوَ صَائِمٌ, فَمَرَّ بِهِ اَلنَّبِيُّ صلى الله عليه وسلم فَقَالَ: " أَفْطَرَ هَذَانِ ", ثُمَّ رَخَّصَ اَلنَّبِيُّ صلى الله عليه وسلم بَعْدُ فِي اَلْحِجَامَةِ لِلصَّائِمِ, وَكَانَ أَنَسٌ يَحْتَجِمُ وَهُوَ صَائِمٌ   رَوَاهُ اَلدَّارَقُطْنِيُّ وَقَوَّاهُ',
      'id' => 'BM.687',
    ),
    1249 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 687',
      'content' => 'Dari \'Aisyah -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam memakai celak mata pada bulan Ramadhan sewaktu beliau shaum. Riwayat Ibnu Majah dengan sanad yang lemah. Tirmidzi berkata: Dalam bab ini tidak ada hadits yang shahih.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا,  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم اِكْتَحَلَ فِي رَمَضَانَ, وَهُوَ صَائِمٌ   رَوَاهُ اِبْنُ مَاجَهْ بِإِسْنَادٍ ضَعِيفٍ. قَالَ اَلتِّرْمِذِيُّ: لَا يَصِحُّ فِيهِ شَيْءٌ',
      'id' => 'BM.687',
    ),
    1250 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 688',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa lupa bahwa ia sedang shaum, lalu ia makan dan minum, hendaknya ia meneruskan puasanya, karena sesungguhnya ia telah diberi makan dan minum oleh Allah." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَنْ نَسِيَ وَهُوَ صَائِمٌ, فَأَكَلَ أَوْ شَرِبَ, فَلْيُتِمَّ صَوْمَهُ, فَإِنَّمَا أَطْعَمَهُ اَللَّهُ وَسَقَاهُ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.688',
    ),
    1251 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 689',
      'content' => 'Menurut riwayat Hakim: "Barangsiapa yang berbuka pada saat puasa Ramadhan karena lupa, maka tak ada qodlo dan kafarat baginya." Hadits Shahih.',
      'arabic' => 'وَلِلْحَاكِمِ:  مَنْ أَفْطَرَ فِي رَمَضَانَ نَاسِيًا فَلَا قَضَاءَ عَلَيْهِ وَلَا كَفَّارَةَ   وَهُوَ صَحِيحٌ',
      'id' => 'BM.689',
    ),
