    1265 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 700',
      'content' => 'Dari Abu Said Al-Khudry -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Jika seorang hamba shaum sehari waktu perang di jalan Allah, niscaya Allah akan menjauhkannya dengan puasa itu dan diterima dari api neraka sejauh 70 tahun perjalanan." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي سَعِيدٍ اَلْخُدْرِيِّ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَا مِنْ عَبْدٍ يَصُومُ يَوْمًا فِي سَبِيلِ اَللَّهِ إِلَّا بَاعَدَ اَللَّهُ بِذَلِكَ اَلْيَوْمِ عَنْ وَجْهِهِ اَلنَّارَ سَبْعِينَ خَرِيفًا   مُتَّفَقٌ عَلَيْهِ, وَاللَّفْظُ لِمُسْلِمٍ',
      'id' => 'BM.700',
    ),
    1266 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 701',
      'content' => '\'Aisyah -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam biasa shaum sehingga kami menyangka beliau tidak akan berbuka dan beliau berbuka sehingga kami menyangka beliau tidak akan shaum. Dan aku tidak pernah melihat Rasulullah Shallallaahu \'alaihi wa Sallam menyempurnakan puasa sebulan penuh kecuali bulan Ramadhan dan aku tidak pernah melihat beliau shaum dalam suatu bulan lebih banyak daripada bulan Sya\'ban. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  كَانَ رَسُولُ اَللَّهِ صلى الله عليه وسلم يَصُومُ حَتَّى نَقُولَ لَا يُفْطِرُ, وَيُفْطِرُ حَتَّى نَقُولَ لَا يَصُومُ, وَمَا رَأَيْتُ رَسُولَ اَللَّهِ صلى الله عليه وسلم اِسْتَكْمَلَ صِيَامَ شَهْرٍ قَطُّ إِلَّا رَمَضَانَ, وَمَا رَأَيْتُهُ فِي شَهْرٍ أَكْثَرَ مِنْهُ صِيَامًا فِي شَعْبَانَ   مُتَّفَقٌ عَلَيْهِ، وَاللَّفْظُ لِمُسْلِمٍ',
      'id' => 'BM.701',
    ),
    1267 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 702',
      'content' => 'Abu Dzar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam memerintahkan kita untuk shaum tiga hari dalam sebulan, yaitu pada tanggal 13,14, dan 15. Riwayat Nasa\'i dan Tirmidzi. Hadits shahih menurut Ibnu Hibban.',
      'arabic' => 'وَعَنْ أَبِي ذَرٍّ رضي الله عنه قَالَ:  أَمَرَنَا رَسُولُ اَللَّهِ صلى الله عليه وسلم أَنْ نَصُومَ مِنْ اَلشَّهْرِ ثَلَاثَةَ أَيَّامٍ: ثَلَاثَ عَشْرَةَ, وَأَرْبَعَ عَشْرَةَ وَخَمْسَ عَشْرَةَ   رَوَاهُ النَّسَائِيُّ, وَاَلتِّرْمِذِيُّ, وَصَحَّحَهُ اِبْنُ حِبَّانَ',
      'id' => 'BM.702',
    ),
    1268 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 703',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Tidak diperbolehkan bagi seorang perempuan shaum di saat suaminya di rumah, kecuali dengan seizinnya." (HR. Muttafaqun \'Alaih dan lafadznya menurut riwayat al-Bukhari). Abu Dawud menambahkan: "Kecuali pada bulan Ramadhan."',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَالَ:  لَا يَحِلُّ لِلْمَرْأَةِ أَنْ تَصُومَ وَزَوْجُهَا شَاهِدٌ إِلَّا بِإِذْنِهِ   مُتَّفَقٌ عَلَيْهِ, وَاللَّفْظُ لِلْبُخَارِيِّ. وَزَادَ أَبُو دَاوُدَ:  غَيْرَ رَمَضَانَ',
      'id' => 'BM.703',
    ),
    1269 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 704',
      'content' => 'Dari Abu Said Al-Khudry bahwa Rasulullah Shallallaahu \'alaihi wa Sallam melarang shaum pada dua hari, yakni hari raya Fithri dan hari raya Kurban. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي سَعِيدٍ اَلْخُدْرِيِّ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم نَهَى عَنْ صِيَامِ يَوْمَيْنِ: يَوْمِ اَلْفِطْرِ وَيَوْمِ اَلنَّحْرِ   مُتَّفَقٌ عَلَيْهِ.',
