    1271 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 706',
      'content' => 'Aisyah dan Ibnu Umar -Semoga Allah meridlainya- berkata: Tidak diizinkan shaum pada hari-hari tasyriq, kecuali orang yang tidak mendapatkan hewan kurban di Mina saat ibadah haji. Riwayat Bukhari.',
      'arabic' => 'وَعَنْ عَائِشَةَ وَابْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمْ قَالَا:  لَمْ يُرَخَّصْ فِي أَيَّامِ اَلتَّشْرِيقِ أَنْ يُصَمْنَ إِلَّا لِمَنْ لَمْ يَجِدِ اَلْهَدْيَ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.706',
    ),
    1272 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 707',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Janganlah mengkhususkan malam Jum\'at untuk bangun beribadah dibanding malam-malam lainnya dan janganlah mengkhususkan hari Jum\'at untuk shaum dibanding hari-hari yang lainnya, kecuali jika seseorang di antara kamu sudah terbiasa shaum." Diriwayatkan oleh Muslim.',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه عَنِ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  لَا تَخْتَصُّوا لَيْلَةَ اَلْجُمُعَةِ بِقِيَامٍ مِنْ بَيْنِ اَللَّيَالِي, وَلَا تَخْتَصُّوا يَوْمَ اَلْجُمُعَةِ بِصِيَامٍ مِنْ بَيْنِ اَلْأَيَّامِ, إِلَّا أَنْ يَكُونَ فِي صَوْمٍ يَصُومُهُ أَحَدُكُمْ   رَوَاهُ مُسْلِمٌ',
      'id' => 'BM.707',
    ),
    1273 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab Shaum Sunnah dan Shaum Yang Dilarang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 708',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Janganlah sekali-kali seseorang di antara kamu shaum pada hari Jum\'at, kecuali ia shaum sehari sebelumnya atau sehari sesudahnya." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  لَا يَصُومَنَّ أَحَدُكُمْ يَوْمَ اَلْجُمُعَةِ, إِلَّا أَنْ يَصُومَ يَوْمًا قَبْلَهُ, أَوْ يَوْمًا بَعْدَهُ   مُتَّفَقٌ عَلَيْهِ',
