    1287 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 720',
      'content' => '\'Aisyah -Semoga Allah meridlainya- berkata: Disunatkan bagi orang yang beri\'tikaf untuk tidak menjenguk orang sakit, tidak melawat jenazah, tidak menyentuh perempuan dan tidak juga menciumnya, tidak keluar masjid untuk suatu keperluan kecuali keperluan yang sangat mendesak, tidak boleh i\'tikaf kecuali dengan shaum, dan tidak boleh i\'tikaf kecuali di masjid jami\'. Riwayat Abu Dawud. Menurut pendapat yang kuat hadits ini mauquf akhirnya.',
      'arabic' => 'وَعَنْهَا قَالَتْ:  اَلسُّنَّةُ عَلَى اَلْمُعْتَكِفِ أَنْ لَا يَعُودَ مَرِيضًا, وَلَا يَشْهَدَ جِنَازَةً, وَلَا يَمَسَّ امْرَأَةً, وَلَا يُبَاشِرَهَا, وَلَا يَخْرُجَ لِحَاجَةٍ, إِلَّا لِمَا لَا بُدَّ لَهُ مِنْهُ, وَلَا اعْتِكَافَ إِلَّا بِصَوْمٍ وَلَا اعْتِكَافَ إِلَّا فِي مَسْجِدٍ جَامِعٍ   رَوَاهُ أَبُو دَاوُدَ, وَلَا بَأْسَ بِرِجَالِهِ, إِلَّا أَنَّ اَلرَّاجِحَ وَقْفُ آخِرِهِ',
      'id' => 'BM.720',
    ),
    1288 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 721',
      'content' => 'Dari Ibnu Abbas -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Tidak ada kewajiban shaum bagi orang yang i\'tikaf, kecuali ia mewajibkan atas dirinya sendiri." Riwayat Daruquthni dan Hakim. hadits mauquf menurut pendapat yang kuat.',
      'arabic' => 'وَعَنْ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا; أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم قَالَ:  لَيْسَ عَلَى اَلْمُعْتَكِفِ صِيَامٌ إِلَّا أَنْ يَجْعَلَهُ عَلَى نَفْسِهِ   رَوَاهُ اَلدَّارَقُطْنِيُّ وَالْحَاكِمُ, وَالرَّاجِحُ وَقْفُهُ أَيْضًا',
      'id' => 'BM.721',
    ),
    1289 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 722',
      'content' => 'Dari Ibnu Umar -Semoga Allah meridlainya- bahwa beberapa shahabat Nabi Shallallaahu \'alaihi wa Sallam melihat lailatul qadr dalam mimpi tujuh malam terakhir, maka Rasulallah Shallallahu \'Alaihi wa Sallam bersabda, Aku telah ditunjukkan kebenaran tentang mimpimu pada tujuh malam yang terakhir, maka barangsiapa ingin mencarinya hendaknya ia mencari pada tujuh malam terakhir." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ رَضِيَ اَللَّهُ عَنْهُمَا:  أَنَّ رِجَالاً مِنْ أَصْحَابِ اَلنَّبِيِّ صلى الله عليه وسلم أُرُوا لَيْلَةَ اَلْقَدْرِ فِي اَلْمَنَامِ, فِي اَلسَّبْعِ اَلْأَوَاخِرِ, فَقَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم أَرَى رُؤْيَاكُمْ قَدْ تَوَاطَأَتْ فِي اَلسَّبْعِ اَلْأَوَاخِرِ, فَمَنْ كَانَ مُتَحَرِّيَهَا فَلْيَتَحَرَّهَا فِي اَلسَّبْعِ اَلْأَوَاخِرِ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.722',
    ),
    1290 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 723',
      'content' => 'Dari Muawiyah Ibnu Abu Sufyan -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda tentang lailatul qadar: "Malam dua puluh tujuh." Riwayat Abu Dawud dan menurut pendapat yang kuat ia adalah mauquf. ada 40 pendapat yang berselisih tentang penetapannya yang saya paparkan dalam kitab Fathul Bari.',
      'arabic' => 'وَعَنْ مُعَاوِيَةَ بْنَ أَبِي سُفْيَانَ رَضِيَ اَللَّهُ عَنْهُمَا, عَنْ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ فِي لَيْلَةِ الْقَدْرِ:  لَيْلَةُ سَبْعٍ وَعِشْرِينَ   رَوَاهُ أَبُو دَاوُدَ, وَالرَّاجِحُ وَقْفُهُ. وَقَدْ اِخْتُلِفَ فِي تَعْيِينِهَا عَلَى أَرْبَعِينَ قَوْلًا أَوْرَدْتُهَا فِي فَتْحِ اَلْبَارِي',
      'id' => 'BM.723',
    ),
    1291 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 724',
      'content' => 'Dari \'Aisyah -Semoga Allah meridlainya- bahwa dia bertanya: Wahai Rasulullah, bagaimana jika aku tahu suatu malam dan diterima dari lailatul qadr, apa yang harus aku baca pada malam tersebut? Beliau bersabda: "bacalah artinya: Ya Allah, sesungguhnya Engkau Maha Pengampun, Engkau menyukai ampunan, maka ampunilah aku." Riwayat Imam Lima selain Abu Dawud. Hadits shahih menurut Tirmidzi dan Hakim.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  قُلْتُ يَا رَسُولَ اَللَّهِ : أَرَأَيْتَ إِنْ عَلِمْتُ أَيَّ لَيْلَةٍ لَيْلَةُ اَلْقَدْرِ, مَا أَقُولُ فِيهَا? قَالَ: قُولِي: اَللَّهُمَّ إِنَّكَ عَفُوٌّ تُحِبُّ اَلْعَفْوَ فَاعْفُ عَنِّي "   رَوَاهُ اَلْخَمْسَةُ, غَيْرَ أَبِي دَاوُدَ, وَصَحَّحَهُ اَلتِّرْمِذِيُّ, وَالْحَاكِمُ',
      'id' => 'BM.724',
    ),
    1292 => 
    array (
      'chapter' => 'Kitab ash-Shiyaam (Shaum): Bab I\'tikaf dan Ibadah Ramadlan',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 725',
      'content' => 'Dari Abu Said Al-Khudry -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Tidak ada perjalanan kecuali ke tiga masjid, yaitu Masjidil Haram, Masjidku ini, dan Masjidil Aqsho." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي سَعِيدٍ اَلْخُدْرِيِّ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم :  لَا تُشَدُّ اَلرِّحَالُ إِلَّا إِلَى ثَلَاثَةِ مَسَاجِدَ: اَلْمَسْجِدِ اَلْحَرَامِ, وَمَسْجِدِي هَذَا, وَالْمَسْجِدِ اَلْأَقْصَى   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.725',
    ),
    1293 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Keutamaan Haji dan Yang Berkewajiban Haji',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 726',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Umrah ke umrah menghapus dosa antara keduanya, dan tidak ada pahala bagi haji mabruru kecuali surga." (HR. Muttafaqun \'Alaih).',
      'arabic' => 'عَنْ أَبِي هُرَيْرَةَ رضي الله عنه أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم قَالَ:  اَلْعُمْرَةُ إِلَى اَلْعُمْرَةِ كَفَّارَةٌ لِمَا بَيْنَهُمَا, وَالْحَجُّ اَلْمَبْرُورُ لَيْسَ لَهُ جَزَاءٌ إِلَّا اَلْجَنَّةَ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.726',
    ),
    1294 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Keutamaan Haji dan Yang Berkewajiban Haji',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 727',
      'content' => 'Dari \'Aisyah -Semoga Allah meridlainya- bahwa dia bertanya: Wahai Rasulullah, apakah kaum wanita itu diwajibkan jihad? Beliau menjawab: Ya, mereka diwajibkan jihad tanpa perang di dalamnya, yaitu haji dan umrah." Riwayat Ahmad dan Ibnu Majah dengan lafadz menurut riwayatnya. Sanadnya shahih dan asalnya dan diterima dari shahih Bukhari-Muslim.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  قُلْتُ: يَا رَسُولَ اَللَّهِ! عَلَى اَلنِّسَاءِ جِهَادٌ ? قَالَ: نَعَمْ, عَلَيْهِنَّ جِهَادٌ لَا قِتَالَ فِيهِ: اَلْحَجُّ, وَالْعُمْرَةُ   رَوَاهُ أَحْمَدُ, وَابْنُ مَاجَهْ وَاللَّفْظُ لَهُ, وَإِسْنَادُهُ صَحِيحٌ. وَأَصْلُهُ فِي اَلصَّحِيحِ',
      'id' => 'BM.727',
    ),
    1295 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Keutamaan Haji dan Yang Berkewajiban Haji',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 728',
      'content' => 'Dari Jabir Ibnu Abdullah -Semoga Allah meridlainya- bahwa ada seorang Arab Badui datang kepada Nabi Shallallaahu \'alaihi wa Sallam lalu berkata: Wahai Rasulullah, beritahukanlah aku tentang umrah, apakah ia wajib? Beliau bersabda: "Tidak, namun jika engkau berumrah, itu lebih baik bagimu." Riwayat Ahmad dan Tirmidzi. Menurut pendapat yang kuat hadits ini mauquf. Ibnu Adiy mengeluarkan hadits dan diterima dari jalan lain yang lemah, dan diterima dari Jabir -Semoga Allah meridlainya- berupa hadits marfu\': Haji dan umrah adalah wajib.',
      'arabic' => 'وَعَنْ جَابِرِ بْنِ عَبْدِ اَللَّهِ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  أَتَى اَلنَّبِيَّ صلى الله عليه وسلم أَعْرَابِيٌّ. فَقَالَ: يَا رَسُولَ اَللَّهِ! أَخْبِرْنِي عَنْ اَلْعُمْرَةِ, أَوَاجِبَةٌ هِيَ? فَقَالَ: لَا وَأَنْ تَعْتَمِرَ خَيْرٌ لَكَ   رَوَاهُ أَحْمَدُ, وَاَلتِّرْمِذِيُّ, وَالرَّاجِحُ وَقْفُهُ. وَأَخْرَجَهُ اِبْنُ عَدِيٍّ مِنْ وَجْهٍ آخَرَ ضَعِيفٍ عَنْ جَابِرٍ مَرْفُوعًا:  اَلْحَجُّ وَالْعُمْرَةُ فَرِيضَتَانِ',
      'id' => 'BM.728',
    ),
    1296 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Keutamaan Haji dan Yang Berkewajiban Haji',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 729',
      'content' => 'Anas -Semoga Allah meridlainya- berkata: Ada yang bertanya: Wahai Rasulullah, apakah sabil jalan itu? beliau bersabda: "Bekal dan kendaraan." Riwayat Daruquthni. Hadits shahih menurut Hakim. Hadits mursal menuru pendapat yang kuat.',
      'arabic' => 'وَعَنْ أَنَسٍ رضي الله عنه قَالَ:  قِيلَ يَا رَسُولَ اَللَّهِ, مَا اَلسَّبِيلُ? قَالَ: اَلزَّادُ وَالرَّاحِلَةُ   رَوَاهُ اَلدَّارَقُطْنِيُّ وَصَحَّحَهُ اَلْحَاكِمُ, وَالرَّاجِحُ إِرْسَالُهُ',
      'id' => 'BM.729',
    ),
    1297 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Perkara Yang Membatalkan Wudlu',
      'source' => 'Bulughul Maram',
