    1343 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 771',
      'content' => 'Anas -Semoga Allah meridlainya- berkata: Di antara kami ada yang membaca talbiyah dan tidak ada yang melarangnya, dan ada yang membaca takbir dan tidak ada yang melarangnya. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَنَسٍ رضي الله عنه قَالَ:  كَانَ يُهِلُّ مِنَّا اَلْمُهِلُّ فَلَا يُنْكِرُ عَلَيْهِ, وَيُكَبِّرُ ] مِنَّا [ اَلْمُكَبِّرُ فَلَا يُنْكِرُ عَلَيْهِ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.771',
    ),
    1344 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 772',
      'content' => 'Ibnu Abbas -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam mengutusku untuk membawa barang-barang berat, atau ia berkata untuk menyertai perempuan-perempuan yang lemah dan diterima dari Mudzalifah pada waktu malam. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ:  بَعَثَنِي رَسُولُ اَللَّهِ صلى الله عليه وسلم فِي اَلثَّقَلِ, أَوْ قَالَ فِي اَلضَّعَفَةِ مِنْ جَمْعٍ بِلَيْلٍ',
      'id' => 'BM.772',
    ),
    1345 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 773',
      'content' => '\'Aisyah -Semoga Allah meridlainya- berkata: Saudah pernah minta izin Rasulullah Shallallaahu \'alaihi wa Sallam pada malam Mudzalifah untuk berangkat lebih dahulu karena dia lemah --yakni berat berjalan-- dan beliau mengizinkannya. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  اِسْتَأْذَنَتْ سَوْدَةُ رَسُولَ اَللَّهِ صلى الله عليه وسلم لَيْلَةَ اَلْمُزْدَلِفَةِ: أَنْ تَدْفَعَ قَبْلَهُ, وَكَانَتْ ثَبِطَةً -تَعْنِي: ثَقِيلَةً- فَأَذِنَ لَهَا   مُتَّفَقٌ عَلَيْهِمَا',
      'id' => 'BM.773',
    ),
    1346 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 774',
      'content' => 'Ibnu Abbas -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam bersabda pada kami: "Janganlah melempar Jumrah hingga matahari terbit." Riwayat Imam Lima kecuali Nasa\'i. Hadits Munqathi\'.',
      'arabic' => 'وَعَنْ اِبْنِ عَبَّاسٍ رَضِيَ اَللَّهُ عَنْهُمَا قَالَ: قَالَ لَنَا رَسُولُ اَللَّهِ صلى الله عليه وسلم  لَا تَرْمُوا اَلْجَمْرَةَ حَتَّى تَطْلُعَ اَلشَّمْسُ   رَوَاهُ اَلْخَمْسَةُ إِلَّا النَّسَائِيَّ, وَفِيهِ اِنْقِطَاعٌ',
      'id' => 'BM.774',
    ),
    1347 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 775',
      'content' => '\'Aisyah -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam mengutus Ummu Salamah pada malam hari raya Kurban, lalu ia melempar Jumrah sebelum fajar, kemudian pergi dan turun ke Mekkah. Riwayat Abu Dawud dan sanadnya menurut syarat Muslim.',
      'arabic' => 'وَعَنْ عَائِشَةَ رَضِيَ اَللَّهُ عَنْهَا قَالَتْ:  أَرْسَلَ اَلنَّبِيُّ صلى الله عليه وسلم بِأُمِّ سَلَمَةَ لَيْلَةَ اَلنَّحْرِ, فَرَمَتِ اَلْجَمْرَةَ قَبْلَ اَلْفَجْرِ, ثُمَّ مَضَتْ فَأَفَاضَتْ   رَوَاهُ أَبُو دَاوُدَ, وَإِسْنَادُهُ عَلَى شَرْطِ مُسْلِمٍ',
      'id' => 'BM.775',
    ),
    1348 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 776',
      'content' => 'Dari Urwah Ibnu Mudlorras -Semoga Allah meridlainya- ia berkata: telah bersabda Rasulullah Shallallahu \'Alaihi wa Sallam, "Barangsiapa mengikuti sholat kami ini --yakni di Mudzalifah-- lalu bermalam bersama kami hingga kami berangkat, dan sebelum itu ia benar-benar telah wukuf di Arafah malam atau siang maka hajinya telah sempurna dan ia telah menghilangkan kotorannya. Riwayat Imam Lima. Hadits shahih menurut Tirmidzi dan Ibnu Khuzaimah.',
      'arabic' => 'وَعَنْ عُرْوَةَ بْنِ مُضَرِّسٍ رضي الله عنه قَالَ: قَالَ رَسُولُ اَللَّهِ صلى الله عليه وسلم  مَنْ شَهِدَ صَلَاتَنَا هَذِهِ -يَعْنِي: بِالْمُزْدَلِفَةِ- فَوَقَفَ مَعَنَا حَتَّى نَدْفَعَ, وَقَدْ وَقَفَ بِعَرَفَةَ قَبْلَ ذَلِكَ لَيْلاً أَوْ نَهَارًا, فَقَدْ تَمَّ حَجُّهُ وَقَضَى تَفَثَهُ   رَوَاهُ اَلْخَمْسَةُ, وَصَحَّحَهُ اَلتِّرْمِذِيُّ, وَابْنُ خُزَيْمَةَ',
      'id' => 'BM.776',
    ),
    1349 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 777',
      'content' => 'Umar -Semoga Allah meridlainya- berkata: Orang-orang musyrik tidak turun ke Mekkah hingga matahari terbit - dan mereka berkata: Merekalah gunung Tsabir gunung tertinggi di Mekkah dan Nabi Shallallaahu \'alaihi wa Sallam menentang mereka. Maka beliau turun ke Mekkah sebelum matahari terbit. Riwayat Bukhari.',
      'arabic' => 'وَعَنْ عُمَرَ رضي الله عنه قَالَ:  إِنَّ اَلْمُشْرِكِينَ كَانُوا لَا يُفِيضُونَ حَتَّى تَطْلُعَ اَلشَّمْسُ، وَيَقُولُونَ: أَشْرِقْ ثَبِيرُ وَأَنَّ اَلنَّبِيَّ صلى الله عليه وسلم خَالَفَهُمْ, ثُمَّ أَفَاضَ قَبْلَ أَنْ تَطْلُعَ اَلشَّمْسُ   رَوَاهُ اَلْبُخَارِيُّ',
      'id' => 'BM.777',
    ),
    1350 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 778',
      'content' => 'Ibnu Abbas dan Usamah Ibnu Zaid -Semoga Allah meridlainya- berkata: Nabi Shallallaahu \'alaihi wa Sallam selalu tetap membaca talbiyah hingga beliau melempar Jumrah aqabah. Riwayat Bukhari.',
      'arabic' => 'وَعَنْ اِبْنِ عَبَّاسٍ وَأُسَامَةَ بْنِ زَيْدٍ رَضِيَ اَللَّهُ عَنْهُمْ قَالَا:  لَمْ يَزَلِ اَلنَّبِيُّ صلى الله عليه وسلم يُلَبِّي حَتَّى رَمَى جَمْرَةَ اَلْعَقَبَةِ   رَوَاهُ اَلْبُخَارِيُّ.',
      'id' => 'BM.778',
    ),
    1351 => 
    array (
      'chapter' => 'Kitab al-Hajj (Haji): Bab Sifat Haji dan Masuk Kota Makkah',
