    1396 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Syarat-syarat Jual Beli dan hal-hal yang di larang',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 819',
      'content' => 'Menurut riwayat Abu Dawud: Barangsiapa melakukan dua jual-beli dalam satu transaksi, maka baginya harga yang murah atau ia termasuk riba\'.',
      'arabic' => 'وَلِأَبِي دَاوُدَ:  مَنْ بَاعَ بَيْعَتَيْنِ فِي بَيْعَةٍ فَلَهُ أَوَكَسُهُمَا, أَوْ اَلرِّبَا',
      'id' => 'BM.819',
    ),
    1397 => 
    array (
      'chapter' => 'Kitab ath-Thaharah: Bab Perkara Yang Membatalkan Wudlu',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 82',
