    1442 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 860',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Aku mendengar Rasulullah Shallallaahu \'alaihi wa Sallam bersabda: "Jika engkau sekalian berjual-beli dengan \'inah hanya sekedar mengejar keuntungan materi belaka, selalu membuntuti ekor-ekor sapi, hanya puas menunggui tanaman, dan meninggalkan jihad maka Allah akan meliputi dirimu dengan suatu kehinaan yang tidak akan dicabut sebelum kamu kembali kepada agamamu." Diriwayatkan oleh Abu Dawud dan diterima dari Nafi\', dan dalam sanadnya ada pembicaraan. Ahmad meriwayatkan dan diterima dari Atho\' dengan perawi-perawi yang dapat dipercaya dan dinilai shahih oleh Ibnu Qoththon.',
      'arabic' => 'وَعَنِ اِبْنِ عُمَرَ -رَضِيَ اَللَّهُ عَنْهُمَا- قَالَ: سَمِعْتُ رَسُولَ اَللَّهِ صلى الله عليه وسلم يَقُولُ:  إِذَا تَبَايَعْتُمْ بِالْعِينَةِ, وَأَخَذْتُمْ أَذْنَابَ اَلْبَقَرِ, وَرَضِيتُمْ بِالزَّرْعِ, وَتَرَكْتُمْ اَلْجِهَادَ, سَلَّطَ اَللَّهُ عَلَيْكُمْ ذُلًّا لَا يَنْزِعُهُ حَتَّى تَرْجِعُوا إِلَى دِينِكُمْ   رَوَاهُ أَبُو دَاوُدَ مِنْ رِوَايَةِ نَافِعٍ عَنْهُ, وَفِي إِسْنَادِهِ مَقَالٌ. وَلِأَحْمَدَ: نَحْوُهُ مِنْ رِوَايَةِ عَطَاءٍ, وَرِجَالُهُ ثِقَاتٌ وَصَحَّحَهُ اِبْنُ اَلْقَطَّانِِ',
      'id' => 'BM.860',
    ),
    1443 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 861',
      'content' => 'Dari Abu Umamah -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam bersabda: "Barangsiapa memberi syafa\'at menjadi perantara untuk suatu kebaikan kepada saudaranya, lalu ia diberi hadiah dan diterimanya, maka ia telah mendatangi sebuah pintu besar dan diterima dari pintu-pintu riba." Riwayat Ahmad dan Abu Dawud, dan dalam sanadnya ada pembicaraan.',
      'arabic' => 'وَعَنْ أَبِي أُمَامَةَ رضي الله عنه عَنِ اَلنَّبِيِّ صلى الله عليه وسلم قَالَ:  مَنْ شَفَعَ لِأَخِيهِ شَفَاعَةً, فَأَهْدَى لَهُ هَدِيَّةً, فَقَبِلَهَا, فَقَدْ أَتَى بَابًا عَظِيماً مِنْ أَبْوَابِ اَلرِّبَا   رَوَاهُ أَحْمَدُ, وَأَبُو دَاوُدَ, وَفِي إِسْنَادِهِ مَقَالٌ',
      'id' => 'BM.861',
    ),
    1444 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 862',
      'content' => 'Dari Abdullah Ibnu Amar Ibnu al-\'Ash -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam melaknat orang yang memberi dan menerima suap. Riwayat Abu Dawud dan Tirmidzi. Hadits shahih menurut Tirmidzi.',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بْنِ عَمْرِوٍ -رَضِيَ اَللَّهُ عَنْهُمَا- قَالَ:  لَعَنَ رَسُولُ اَللَّهِ صلى الله عليه وسلم اَلرَّاشِي وَالْمُرْتَشِيَ   رَوَاهُ أَبُو دَاوُدَ, وَاَلتِّرْمِذِيُّ وَصَحَّحَهُ',
      'id' => 'BM.862',
    ),
    1445 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 863',
      'content' => 'Dari Abdullah Ibnu Amar Ibnu al-\'Ash -Semoga Allah meridlainya- bahwa Nabi Shallallaahu \'alaihi wa Sallam menyuruhnya untuk menyiapkan pasukan tentara, tetapi unta-unta telah habis. Lalu beliau menyuruhnya agar menghutang dan diterima dari unta zakat. Ia berkata: Aku menghutang seekor unta akan dibayar dengan dua ekor unta zakat. Riwayat Hakim dan Baihaqi dengan perawi-perawi yang dapat dipercaya.',
      'arabic' => 'وَعَنْ عَبْدِ اَللَّهِ بْنِ عَمْرٍو -رَضِيَ اَللَّهُ عَنْهُمَا-;  أَنَّ رَسُولَ صلى الله عليه وسلم أَمَرَهُ أَنْ يُجَهِّزَ جَيْشًا فَنَفِدَتْ اَلْإِبِلُ، فَأَمَرَهُ أَنْ يَأْخُذَ عَلَى قَلَائِصِ اَلصَّدَقَةِ. قَالَ: فَكُنْتُ آخُذُ اَلْبَعِيرَ بِالْبَعِيرَيْنِ إِلَى إِبِلِ اَلصَّدَقَةِ   رَوَاهُ اَلْحَاكِمُ وَالْبَيْهَقِيُّ, وَرِجَالُهُ ثِقَاتٌ',
      'id' => 'BM.863',
    ),
    1446 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 864',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam melarang jual-beli muzabanah, yaitu seseorang yang menjual buah kebunnya, jika kurma basah dijual dengan kurma kering bertakar, anggur basah dijual dengan anggur kering bertakar, dan tanaman kering dijual dengan makanan kering bertakar. Beliau melarang itu semua. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ اِبْنِ عُمَرَ -رَضِيَ اَللَّهُ عَنْهُمَا- قَالَ:  نَهَى رَسُولُ اَللَّهِ صلى الله عليه وسلم عَنِ الْمُزَابَنَةِ; أَنْ يَبِيعَ ثَمَرَ حَائِطِهِ إِنْ كَانَ نَخْلاً بِتَمْرٍ كَيْلاً, وَإِنْ كَانَ كَرْماً أَنْ يَبِيعَهُ بِزَبِيبٍ كَيْلاً, وَإِنْ كَانَ زَرْعاً أَنْ يَبِيعَهُ بِكَيْلِ طَعَامٍ, نَهَى عَنْ ذَلِكَ كُلِّهُ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.864',
    ),
    1447 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 865',
      'content' => 'Sa\'ad Ibnu Abu waqqash -Semoga Allah meridlainya- berkata: Aku mendengar Rasulullah Shallallaahu \'alaihi wa Sallam ditanya tentang hukumnya membeli kurma basah dengan kurma kering. Beliau bersabda: "Apakah kurma basah itu berkurang jika mengering?". Ia menjawab: Ya. Lalu beliau melarang hal itu. Riwayat Imam Lima. Hadits shahih menurut Ibnu al-Madiny, Tirmidzi, Ibnu Hibban, dan Hakim.',
      'arabic' => 'وَعَنْ سَعْدِ بْنِ أَبِي وَقَّاصٍ رضي الله عنه قَالَ:  سَمِعْتُ رَسُولَ اَللَّهِ صلى الله عليه وسلم سُئِلَ عَنِ اِشْتِرَاءِ اَلرُّطَبِ بِالتَّمْرِ فَقَالَ: أَيَنْقُصُ اَلرُّطَبُ إِذَا يَبِسَ?  قَالُوا: نَعَمَ. فَنَهَى عَنْ ذَلِكَ   رَوَاهُ اَلْخَمْسَةُ, وَصَحَّحَهُ اِبْنُ اَلْمَدِينِيِّ, وَاَلتِّرْمِذِيُّ, وَابْنُ حِبَّانَ, وَالْحَاكِمُِ',
      'id' => 'BM.865',
    ),
    1448 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Riba',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 866',
      'content' => 'Dari Ibnu Umar -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam melarang jual-beli yang kemudian dengan yang kemudian, yakni hutang dengan hutang. Riwayat Ishaq dan al-Bazzar dengan sanad lemah.',
      'arabic' => 'وَعَنِ اِبْنِ عُمَرَ -رَضِيَ اَللَّهُ عَنْهُمَا-;  أَنَّ اَلنَّبِيَّ صلى الله عليه وسلم نَهَى عَنْ بَيْعِ اَلْكَالِئِ بِالْكَالِئِ, يَعْنِي: اَلدَّيْنِ بِالدَّيْنِ   رَوَاهُ إِسْحَاقُ, وَالْبَزَّارُ بِإِسْنَادٍ ضَعِيفٍ',
      'id' => 'BM.866',
    ),
    1449 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Rukhshah Jual Beli ar-Raaya\' dan Hukum menjual buah yang masih dipohon',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 867',
      'content' => 'Dari Zaid Ibnu Tsabit -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam memberi keringanan dalam ariyah pohon yang diserahkan perawatannya pada orang lain untuk diambil buahnya; untuk dijual buahnya dengan tangkainya dengan menggunakan takaran. (HR. Muttafaqun \'Alaih). Dan menurut riwayat Muslim: Rasulullah Shallallaahu \'alaihi wa Sallam memberi keringanan dalam ariyah, yaitu penghuni rumah pemilik pohon yang menyerahkan perawatan pohon tersebut kepada orang lain boleh memberi kurma basah dengan kurma kering agar mereka dapat memakan kurma basah.',
      'arabic' => 'عَنْ زَيْدِ بْنِ ثَابِتٍ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم رَخَّصَ فِي اَلْعَرَايَا: أَنْ تُبَاعَ بِخَرْصِهَا كَيْلاً   مُتَّفَقٌ عَلَيْهِ. وَلِمُسْلِمٍ:  رَخَّصَ فِي اَلْعَرِيَّةِ يَأْخُذُهَا أَهْلُ اَلْبَيْتِ بِخَرْصِهَا تَمْرًا، يَأْكُلُونَهَا رُطَبًا',
      'id' => 'BM.867',
    ),
    1450 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Rukhshah Jual Beli ar-Raaya\' dan Hukum menjual buah yang masih dipohon',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 868',
      'content' => 'Dari Abu Hurairah -Semoga Allah meridlainya- bahwa Rasulullah Shallallaahu \'alaihi wa Sallam memberi keringanan menjual buah kurma ariyah yang masih ditangkainya basah dengan kurma kering selama masih kurang dan diterima dari lima wasaq 1 wasaq : 21 kg. (HR. Muttafaqun \'Alaih).',
      'arabic' => 'وَعَنْ أَبِي هُرَيْرَةَ رضي الله عنه  أَنَّ رَسُولَ اَللَّهِ صلى الله عليه وسلم رَخَّصَ فِي بَيْعِ اَلْعَرَايَا بِخَرْصِهَا, فِيمَا دُونَ خَمْسَةِ أَوْسُقٍ, أَوْ فِي خَمْسَةِ أَوْسُقٍ   مُتَّفَقٌ عَلَيْهِ',
      'id' => 'BM.868',
    ),
    1451 => 
    array (
      'chapter' => 'Kitab al-Buyu\' (Jual Beli): Bab Rukhshah Jual Beli ar-Raaya\' dan Hukum menjual buah yang masih dipohon',
      'source' => 'Bulughul Maram',
      'hadith_no' => 'Hadits No. 869',
      'content' => 'Ibnu Umar -Semoga Allah meridlainya- berkata: Rasulullah Shallallaahu \'alaihi wa Sallam melarang menjual buah-buahan yang belum kelihatan baik. Beliau melarang penjual dan pembeli. (HR. Muttafaqun \'Alaih). Dan dalam suatu riwayat: Apabila beliau ditanya tentang buah yang baik, beliau bersabda: "Sampai penyakitnya hilang".',
      'arabic' => 'وَعَنِ اِبْنِ عُمَرَ -رَضِيَ اَللَّهُ عَنْهُمَا- قَالَ:  نَهَى رَسُولُ اَللَّهِ صلى الله عليه وسلم عَنْ بَيْعِ اَلثِّمَارِ حَتَّى يَبْدُوَ صَلَاحُهَا, نَهَى اَلْبَائِعَ وَالْمُبْتَاعَ   مُتَّفَقٌ عَلَيْهِ. وَفِي رِوَايَةٍ: وَكَانَ إِذَا سُئِلَ عَنْ صَلَاحِهَا? قَالَ: حَتَّى تَذْهَبَ عَاهَتُهُ',
      'id' => 'BM.869',
    ),
